import React, { useState } from "react";
import { View, Text } from "react-native";
import Globais from "../Configs/Globais";
import { Card } from "react-native-elements";
import { Audio } from "expo-av";
import { Button } from "react-native-elements/dist/buttons/Button";
import { Icon } from "react-native-elements/dist/icons/Icon";
export default function PageMusicas() {
  const [som, setSom] = useState(new Audio.Sound());
  const [somStatus, setSomStatus] = useState(null);
  async function playSound(url) {
    som.unloadAsync();
    const status = await som.loadAsync({ uri: url }, { shouldPlay: true });
    setSomStatus(status);

    if (!status.isPlaying) {
      return await som.playAsync();
    }
  }
  async function stopSound(url) {
    som.unloadAsync();
    const status = await som.loadAsync({ uri: url }, { shouldPlay: true });
    setSomStatus(status);

    if (!status.isPlaying) {
      return await som.stopAsync();
    }
  }
  return (
    <View>
      <Card>
        <Card.Title style={{ color: "#43C1C4", fontFamily: "Porkys" }}>
          MÃºsicas
        </Card.Title>
        <Card.Divider />
        {Globais.playlist.map((item, index) => (
          <View style={{ flexDirection: "row" }} key={index}>
            <View style={{ flex: 0.8 }}>
              <View style={{ flexDirection: "row" }}>
                <View style={{ flex: 0.2 }}>
                  <Icon name="music" type="font-awesome" />
                </View>
                <View style={{ flex: 0.8 }}>
                  <Text>{item.nome}</Text>
                </View>
              </View>
            </View>
            <View style={{ flex: 0.2 }}>
              {item.current == 1 ? (
                <Button
                  onPress={() => stopSound(item.url_musica)}
                  title=""
                  icon={{ name: "stop", type: "font-awesome" }}
                />
              ) : (
                <Button
                  onPress={() => playSound(item.url_musica)}
                  title=""
                  icon={{ name: "play", type: "font-awesome" }}
                />
              )}
            </View>
          </View>
        ))}
      </Card>
    </View>
  );
}
