import React, { Component, useEffect, useState } from "react";
import { ScrollView, View, Text, Touchable } from "react-native";
import { TouchableOpacity } from "react-native-gesture-handler";
import Icone from "../Configs/Icone";
import { Card } from "react-native-elements";
import Banner from "./Banner";
import Globais from "../Configs/Globais";
export default ({ navigation }) => {
  useEffect(() => {
    carregarimagens();
  }, []);
  const [modalVisible, setModalVisible] = useState(false);
  const [dados, setDados] = useState([]);
  const [carregando, setCarregando] = useState(false);

  function carregarimagens() {
    var url = Globais.url + "informes";
    setCarregando(true);

    fetch(url)
      .then((resp) => resp.json())
      .then((json) => {
        const resultado = json.data.data;
        setDados(resultado);
        // console.log(resultado);
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {
        setCarregando(false);
      });
  }
  return (
    <ScrollView>
      <Card>
        <Banner />
        <Card.Title style={{ color: "#43C1C4" }}>Últimas notícias</Card.Title>
        <Card.Divider />
        <View>
          <Text>asdfasfd</Text>
        </View>
      </Card>
    </ScrollView>
  );
};
