import React from "react";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";

import Icone from "../Configs/Icone";

import PageHome from "../Pages/PageHome";

import PageAgenda from "../Pages/PageAgenda";
import PageCardapio from "../Pages/PageCardapio";
import PageEvento from "../Pages/PageEvento";
import PageNovidades from "../Pages/PageNovidades";
import Calendario from "../Pages/Calendario";
import CalendarioNovo from "../Pages/CalendarioNovo";
import PageBoletim from "../Pages/PageBoletim";

const Tab = createBottomTabNavigator();
export default () => {
  return (
    <Tab.Navigator
      screenOptions={({ route }) => ({
        tabBarIcon: ({ focused, color, size }) => {
          let icone;

          if (route.name === "TabHome") {
            icone = focused ? (
              <Icone estilo={{}} color={"#F38DAD"} name="home" />
            ) : (
              <Icone estilo={{}} color={"#5BA3D9"} name="home" />
            );
          } else if (route.name === "TabAgenda") {
            icone = focused ? (
              <Icone estilo={{}} color={"#F38DAD"} name="calendar" />
            ) : (
              <Icone estilo={{}} color={"#5BA3D9"} name="calendar" />
            );
          } else if (route.name === "TabBoletim") {
            icone = focused ? (
              <Icone estilo={{}} color={"#F38DAD"} name="fire" />
            ) : (
              <Icone estilo={{}} color={"#5BA3D9"} name="fire" />
            );
          } else if (route.name === "TabEventos") {
            icone = focused ? (
              <Icone estilo={{}} color={"#F38DAD"} name="address-book" />
            ) : (
              <Icone estilo={{}} color={"#5BA3D9"} name="address-book" />
            );
          } else if (route.name === "TabNovidades") {
            icone = focused ? (
              <Icone estilo={{}} color={"#F38DAD"} name="file" />
            ) : (
              <Icone estilo={{}} color={"#5BA3D9"} name="file" />
            );
          }

          // You can return any component that you like here!
          return icone;
        },
        tabBarActiveTintColor: "tomato",
        tabBarInactiveTintColor: "gray",
      })}
    >
      <Tab.Screen
        name="TabBoletim"
        component={PageBoletim}
        options={() => ({ title: "Boletim", headerShown: false })}
      />
      <Tab.Screen
        name="TabAgendaDiaria"
        component={PageAgendaDiaria}
        options={() => ({ title: "Agenda Diária", headerShown: false })}
      />
      <Tab.Screen
        name="TabFrequencia"
        component={PageFrequencia}
        options={() => ({ title: "Frequência", headerShown: false })}
      />
      <Tab.Screen
        name="TabAviso"
        component={PageAviso}
        options={() => ({ title: "Avisos", headerShown: false })}
      />
      <Tab.Screen
        name="TabApostilas"
        component={PageApostilas}
        options={() => ({ title: "Apostilas", headerShown: false })}
      />
    </Tab.Navigator>
  );
};
