import React, { useEffect, useState } from "react";
import {
  ScrollView,
  View,
  Image,
  FlatList,
  Text,
  StatusBar,
  StyleSheet,
  SafeAreaView,
  ActivityIndicator,
  Touchable,
} from "react-native";

import Icon from "react-native-vector-icons/Ionicons";
import { TouchableOpacity } from "react-native-gesture-handler";

import { Card, Tab, TabView, FAB } from "react-native-elements";
import Banner from "./Banner";
import BannerPrincipal from "./BannerPrincipal";
import Globais from "../Configs/Globais";
import PageBlocoquemsomos from "./PageBlocoquemsomos";
import PageBlocoInformes from "./PageBlocoInformes";
import PageBlocovcsabia from "./PageBlocovcsabia";
export default ({ navigation }) => {
  const [index, setIndex] = React.useState(0);
  useEffect(() => {
    carregarimagens();
  }, []);

  const [dados, setDados] = React.useState([]);
  const [carregando, setCarregando] = React.useState(false);
  const [ligado, setLigado] = React.useState(false);

  function carregarimagens() {
    var url = Globais.url + "informes";
    setCarregando(true);

    fetch(url)
      .then((resp) => resp.json())
      .then((json) => {
        const resultado = json.data.data;
        setDados(resultado);
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {
        setCarregando(false);
      });
  }
  return (
    <ScrollView>
      <Card
        containerStyle={{
          borderRadius: 10,
        }}
      >
        <Card.Image
          style={styles.image}
          source={require("../assets/images/logo.png")}
        />
      </Card>
      <View
        style={{
          flex: 1,
          alignSelf: "flex-end",
          marginTop: 10,
          marginRight: 10,

          position: "absolute",
        }}
      >
        {ligado ? (
          <Icon name="musical-notes" style={{ fontSize: 15 }} />
        ) : (
          <Icon name="musical-notes-outline" style={{ fontSize: 15 }} />
        )}
      </View>
      <BannerPrincipal />

      <Card>
        <Tab
          value={index}
          onChange={(e) => setIndex(e)}
          indicatorStyle={{
            backgroundColor: "white",
            height: 3,
          }}
          variant="primary"
        >
          <Tab.Item
            title="Informes"
            titleStyle={{ fontSize: 12 }}
            icon={{
              name: "newspaper-outline",
              type: "ionicon",
              color: "white",
            }}
          />
          <Tab.Item
            title="Você Sabia?"
            titleStyle={{ fontSize: 12 }}
            icon={{ name: "help-outline", type: "ionicon", color: "white" }}
          />
          <Tab.Item
            title="Quem Somos"
            titleStyle={{ fontSize: 12 }}
            icon={{
              name: "person-circle-outline",
              type: "ionicon",
              color: "white",
            }}
          />
        </Tab>

        <TabView value={index} onChange={setIndex} animationType="spring">
          <TabView.Item style={{ width: "85%" }}>
            <PageBlocoInformes navigation={navigation} />
          </TabView.Item>
          <TabView.Item style={{ width: "85%" }}>
            <PageBlocovcsabia navigation={navigation} />
          </TabView.Item>
          <TabView.Item style={{ width: "85%" }}>
            <PageBlocoquemsomos navigation={navigation} />
          </TabView.Item>
        </TabView>
      </Card>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: StatusBar.currentHeight,
  },
  scrollView: {
    marginHorizontal: 0,
  },
  text: {
    fontSize: 35,
  },
  titulo: {
    color: "#43C1C4",
    fontSize: 16,
  },
  image: {
    width: "100%",
    height: 300,
    resizeMode: "contain",
  },
  actionButtonIcon: {
    fontSize: 20,
    height: 22,
    color: "white",
  },
});
