import React, { useEffect, useState } from "react";
import Icon from "react-native-vector-icons/FontAwesome";
import { Input, Card, Button } from "react-native-elements";
import { MaskedTextInput } from "react-native-mask-text";
import {
  View,
  Text,
  TextInput,
  StyleSheet,
  Image,
  TouchableOpacity,
} from "react-native";
import Globais from "../Configs/Globais";
import { color } from "react-native-reanimated";

export default ({ navigation, titulo }) => {
  const [nome, setNome] = useState("");
  const [email, setemail] = useState("maria@maria.com.brr");
  const [fone, setFone] = useState("");

  const [temerro, settemErro] = useState(false);
  const [mensagem, setMensagem] = useState("");

  var dadosFormdata = new FormData();
  dadosFormdata.append("nome", email);
  dadosFormdata.append("email", email);
  console.log(dadosFormdata);
  var requestPost = {
    method: "POST",
    body: dadosFormdata,
  };

  function logincandidato() {
    var url = Globais.url + "login";

    fetch(url, requestPost)
      .then((resp) => resp.json())
      .then((json) => {
        let dadosretorno = json;
        console.log(dadosretorno);

        if (dadosretorno.error == "Unauthorized") {
          settemErro(true);
          console.log(requestPost);
          // setNensagem(dadosretorno.info);
        } else {
          Globais.nomeusuario = dadosretorno.nome;
          Globais.emailusuario = dadosretorno.email;
          Globais.logado = true;
          Globais.token = dadosretorno.token;
          Globais.fotousuario = dadosretorno.foto;
          localStorage.setItem("nomeusuario", dadosretorno.nome);
          localStorage.setItem("emailusuario", dadosretorno.email);
          localStorage.setItem("fotousuario", dadosretorno.foto);
          localStorage.setItem("logado", "true");
          localStorage.setItem("token", dadosretorno.token);

          //setDados(json.login);
          navigation.navigate("AppInicio");
        }
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {
        //alert("fim");
      });
  }

  return (
    <View
      style={{
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <Card containerStyle={{ width: "90%", borderRadius: 10 }}>
        <Card.Title style={{ color: "#43C1C4" }}>
          Entre em contato conosco
        </Card.Title>
        <Card.Divider />
        <Input
          onChangeText={(texto) => {
            setNome(texto);
          }}
          style={{ color: "#F38DAD" }}
          value={email}
          placeholder="Nome"
          leftIcon={{ type: "font-awesome", name: "user", color: "#F38DAD" }}
        />
        <Input
          onChangeText={(texto) => {
            setemail(texto);
          }}
          style={{ color: "#F38DAD" }}
          value={email}
          placeholder="Email"
          leftIcon={{
            type: "font-awesome",
            name: "envelope",
            color: "#F38DAD",
          }}
        />

        <MaskedTextInput
          mask="(79)99999-9999"
          onChangeText={(texto) => {
            setFone(texto);
          }}
          style={styles.input}
          placeholder="Whatsapp"
          keyboardType="numeric"
          options={{
            prefix: "$",
          }}
        />
        <Input
          onChangeText={(texto) => {
            setMensagem(texto);
          }}
          style={{ color: "#F38DAD" }}
          value={mensagem}
          placeholder="Envie sua mensagem"
          leftIcon={{
            type: "font-awesome",
            name: "envelope",
            color: "#F38DAD",
          }}
          editable
          numberOfLines={5}
        />
        <Button
          buttonStyle={{ backgroundColor: "#5BA3D9" }}
          onPress={() => logincandidato()}
          icon={<Icon name="arrow-right" size={18} color="white" />}
          title="Entrar"
        />
        {temerro ? (
          <View
            style={{
              width: "100%",
              height: 15,
              alignContent: "center",
              alignItems: "center",
            }}
          >
            <Text
              style={{
                fontSize: 20,
                color: "red",
              }}
            >
              {mensagem}
            </Text>
          </View>
        ) : (
          <View></View>
        )}
      </Card>
    </View>
  );
};

const estilos = StyleSheet.create({
  image: {
    width: 300,
    height: 300,
    resizeMode: "contain",
  },
});
const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    backgroundColor: "#ecf0f1",
    padding: 8,
  },
  input: {
    height: 40,
    margin: 12,
    color: "#F38DAD",
    borderWidth: 1,
  },
});
