import React, { useEffect, useState } from "react";
import { Input, Card, Button } from "react-native-elements";

import {
  View,
  Modal,
  StatusBar,
  StyleSheet,
  ActivityIndicator,
  Image,
  Text,
  SafeAreaView,
  ScrollView,
  Pressable,
  Alert,
  FlatList,
  TouchableOpacity,
} from "react-native";
import { DataTable } from "react-native-paper";
import * as WebBrowser from "expo-web-browser";
import MenuHeader from "../layouts/MenuHeader";

import { obterToken } from "../Configs/Globais";
import Globais from "../Configs/Globais";
import Icone from "../Configs/Icone";

export default ({ navigation, titulo }) => {
  const [dados, setDados] = useState([]);
  const [dadoslogin, setDadoslogin] = useState("");
  const [carregando, setCarregando] = useState(false);
  async function Download(url) {
    await WebBrowser.openBrowserAsync(url);
  }
  async function carregarboletim(token, matricula) {
    var url = Globais.url + "v3/boletim/" + matricula;
    console.log(url);
    setCarregando(true);
    await fetch(url, {
      method: "get",
      headers: new Headers({
        Authorization: "Bearer " + token,
      }),
    })
      .then((resp) => resp.json())
      .then((json) => {
        const resultado = json.data.data;
        console.log(resultado);
        setDados(resultado);
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {
        setCarregando(false);
      });
  }

  useEffect(() => {
    obterToken("dadoslogin").then((resposta) => {
      console.log(resposta);
      carregarboletim(resposta.access_token.accessToken, resposta.id_matricula);
    });
  }, []);

  return (
    <View>
      <MenuHeader navigation={navigation} titulo="Boletim" />

      <ScrollView style={styles.scrollView}>
        <View
          style={{
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <Text>{Globais.nomeusuario}</Text>
          <Card containerStyle={{ width: "95%", borderRadius: 10 }}>
            <DataTable>
              <DataTable.Header style={styles.titulo}>
                <DataTable.Title style={{ flex: 3 }}>Mat√©ria</DataTable.Title>
                <DataTable.Title numeric>Nota1</DataTable.Title>
                <DataTable.Title numeric>Nota2</DataTable.Title>
                <DataTable.Title numeric>Nota3</DataTable.Title>
                <DataTable.Title numeric>Nota4</DataTable.Title>
                <DataTable.Title numeric>Nota5</DataTable.Title>
                <DataTable.Title numeric>Nota6</DataTable.Title>
              </DataTable.Header>

              {carregando ? (
                <View
                  style={{
                    width: Globais.percentuallargura(85),
                    height: Globais.percentualaltura(85),
                    justifyContent: "center",
                    alignItems: "center",
                  }}
                >
                  <ActivityIndicator size="large" animating={true} />
                </View>
              ) : (
                dados.map((item, index) => (
                  <DataTable.Row key={item.id_boletim}>
                    <DataTable.Cell style={styles.titulo}>
                      {item.disciplina}
                    </DataTable.Cell>
                    <DataTable.Cell numeric>{item.nota1}</DataTable.Cell>
                    <DataTable.Cell numeric>{item.nota2}</DataTable.Cell>
                    <DataTable.Cell numeric>{item.nota3}</DataTable.Cell>
                    <DataTable.Cell numeric>{item.nota4}</DataTable.Cell>
                    <DataTable.Cell numeric>{item.nota5}</DataTable.Cell>
                    <DataTable.Cell numeric>{item.nota6}</DataTable.Cell>
                  </DataTable.Row>
                ))
              )}
            </DataTable>
          </Card>
        </View>
      </ScrollView>
    </View>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: StatusBar.currentHeight,
  },
  scrollView: {
    marginHorizontal: 0,
  },
  text: {
    fontSize: 42,
  },
  titulo: {
    color: "#ed3237",
    fontSize: 15,
    backgroundColor: "#f2f2f2",
    flex: 3,
  },
});
