import React, { useEffect, useState } from "react";
import { ScrollView, View, StatusBar, Text, StyleSheet } from "react-native";

import Icon from "react-native-vector-icons/Ionicons";
import { TouchableOpacity } from "react-native-gesture-handler";

import { Card, Tab, TabView, FAB } from "react-native-elements";

import BannerPrincipal from "./BannerPrincipal";
import Globais from "../Configs/Globais";
import PageBlocoquemsomos from "./PageBlocoquemsomos";
import PageBlocoInformes from "./PageBlocoInformes";
import PageBlocovcsabia from "./PageBlocovcsabia";
import { Audio } from "expo-av";
import Popup from "./Popup";
import { Button } from "react-native-elements";

export default ({ navigation }) => {
  const [index, setIndex] = React.useState(0);
  const [som, setSom] = React.useState(new Audio.Sound());
  const [somStatus, setSomStatus] = React.useState(null);
  const [playlist, setPlaylist] = React.useState([{}]);
  const [currentSound, setCurrentSound] = React.useState({});
  const [urlmusica, setUrlMusica] = React.useState("");
  const [carregando, setCarregando] = React.useState(false);
  const [ligado, setLigado] = React.useState(true);

  useEffect(() => {
    for (let i = 0; i < Globais.playlist.length; i++) {
      Globais.playlist[i].current = 0;
    }
    Globais.playlist[0].current = 1;

    Globais.playSound(Globais.urlmusica);
  }, []);

  async function desliga() {
    Globais.stopSound("");
    setLigado(false);
  }

  async function liga() {
    Globais.playSound(Globais.urlmusica);
    setLigado(true);
  }

  return (
    <ScrollView>
      <Button
        onPress={() => navigation.navigate("PageMusicas")}
        title="Músicas"
        icon={{ name: "music", type: "font-awesome", color: "white" }}
      />

      <Card
        containerStyle={{
          borderRadius: 10,
        }}
      >
        <Card.Image
          style={styles.image}
          source={require("../assets/images/logo.png")}
        />
      </Card>

      <View
        style={{
          flex: 1,
          alignSelf: "flex-end",
          marginTop: 10,
          marginRight: 20,

          position: "absolute",
        }}
      >
        {ligado ? (
          <TouchableOpacity onPress={() => desliga()}>
            <Icon
              name="musical-notes"
              style={{ fontSize: 40, color: "#F38DAD" }}
            />
          </TouchableOpacity>
        ) : (
          <TouchableOpacity onPress={() => liga()}>
            <Icon
              name="musical-notes-outline"
              style={{ fontSize: 40, color: "#F38DAD" }}
            />
          </TouchableOpacity>
        )}
      </View>
      <BannerPrincipal />

      <Card>
        <Tab
          value={index}
          onChange={(e) => setIndex(e)}
          indicatorStyle={{
            backgroundColor: "#F38DAD",
            height: 3,
          }}
          variant="primary"
        >
          <Tab.Item
            title="Informes"
            titleStyle={{ fontSize: 12, fontFamily: "Porkys" }}
            icon={{
              name: "newspaper-outline",
              type: "ionicon",
              color: "white",
            }}
          />
          <Tab.Item
            title="Você Sabia?"
            titleStyle={{ fontSize: 12, fontFamily: "Porkys" }}
            icon={{ name: "help-outline", type: "ionicon", color: "white" }}
          />
          <Tab.Item
            title="Quem Somos"
            titleStyle={{ fontSize: 12, fontFamily: "Porkys" }}
            icon={{
              name: "person-circle-outline",
              type: "ionicon",
              color: "white",
            }}
          />
        </Tab>

        <TabView value={index} onChange={setIndex} animationType="spring">
          <TabView.Item style={{ width: "85%" }}>
            <PageBlocoInformes navigation={navigation} />
          </TabView.Item>
          <TabView.Item style={{ width: "85%" }}>
            <PageBlocovcsabia navigation={navigation} />
          </TabView.Item>
          <TabView.Item style={{ width: "85%" }}>
            <PageBlocoquemsomos navigation={navigation} />
          </TabView.Item>
        </TabView>
      </Card>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: StatusBar.currentHeight,
  },
  scrollView: {
    marginHorizontal: 0,
  },
  text: {
    fontSize: 35,
  },
  titulo: {
    color: "#43C1C4",
    fontFamily: "Porkys",
    fontSize: 55,
  },
  image: {
    width: "100%",
    height: 300,
    resizeMode: "contain",
  },
  actionButtonIcon: {
    fontSize: 20,
    height: 22,
    color: "white",
  },
});
