import React, { useEffect, useState } from "react";
import Icon from "react-native-vector-icons/FontAwesome";
import { Input, Card, Button } from "react-native-elements";
import { MaskedTextInput } from "react-native-mask-text";

import {
  View,
  Modal,
  StatusBar,
  StyleSheet,
  ActivityIndicator,
  Image,
  Text,
  SafeAreaView,
  ScrollView,
  Dimensions,
  TouchableOpacity,
} from "react-native";
import Globais from "../Configs/Globais";

export default ({ route, navigation, titulo }) => {
  const [carregando, setCarregando] = useState(false);
  const fotourl = route.params.foto;
  const dimensions = Dimensions.get("window");
  const imageHeight = Math.round((dimensions.width * 9) / 16);
  const imageWidth = dimensions.width;
  return (
    <SafeAreaView style={styles.container}>
      <ScrollView style={styles.scrollView}>
        <View
          style={{
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          {carregando ? (
            <ActivityIndicator size="large" />
          ) : (
            <Card containerStyle={{ width: "90%", borderRadius: 10 }}>
              <Card.Title style={{ color: "#43C1C4" }}>
                <TouchableOpacity
                  onPress={() => navigation.navigate("PageInicioApp")}
                >
                  {" "}
                  Fechar <Icon name="close" />
                </TouchableOpacity>
              </Card.Title>
              <Card.Divider />
              <Image
                source={{ uri: Globais.urladmin + fotourl }}
                style={styles.image}
              />
              <Card.Image
                source={{ uri: Globais.urladmin + fotourl }}
                style={{
                  resizeMode: "contain",
                }}
              />
            </Card>
          )}
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: StatusBar.currentHeight,
  },
  scrollView: {
    marginHorizontal: 0,
  },
  image: {
    flex: 1,
    aspectRatio: 1.5,
    resizeMode: "contain",
  },
});
