import React, { useEffect, useState } from "react";
import {
  ScrollView,
  View,
  Image,
  FlatList,
  Text,
  StatusBar,
  StyleSheet,
  SafeAreaView,
  ActivityIndicator,
  Touchable,
} from "react-native";
import { TouchableOpacity } from "react-native-gesture-handler";

import { Card, Tab, TabView } from "react-native-elements";
import Banner from "./Banner";
import BannerPrincipal from "./BannerPrincipal";
import Globais from "../Configs/Globais";
import PageBlocoquemsomos from "./PageBlocoquemsomos";
import PageBlocoInformes from "./PageBlocoInformes";
export default ({ navigation }) => {
  const [index, setIndex] = React.useState(0);
  useEffect(() => {
    carregarimagens();
  }, []);

  const [dados, setDados] = React.useState([]);
  const [carregando, setCarregando] = React.useState(false);

  function carregarimagens() {
    var url = Globais.url + "informes";
    setCarregando(true);

    fetch(url)
      .then((resp) => resp.json())
      .then((json) => {
        const resultado = json.data.data;
        setDados(resultado);
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {
        setCarregando(false);
      });
  }
  return (
    <ScrollView>
      <Card
        containerStyle={{
          borderRadius: 10,
        }}
      >
        <Card.Image
          style={styles.image}
          source={require("../assets/images/logo.png")}
        />
      </Card>
      <BannerPrincipal />
      <Card>
        <Card.Title style={{ color: "#43C1C4" }}>Últimas Notícias</Card.Title>
        <Card.Divider />

        {carregando ? (
          <View
            style={{
              width: Globais.percentuallargura(85),
              height: Globais.percentualaltura(85),
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <ActivityIndicator size="large" animating={true} />
          </View>
        ) : (
          dados.map((item, index) => (
            <TouchableOpacity
              onPress={() => {
                navigation.navigate("PageVerinformeexterno", {
                  informacoes: item,
                });
              }}
              key={item.id_noticia}
            >
              <View style={{ width: "100%", flexDirection: "row" }}>
                <View style={{ margin: 2, flex: 0.2 }}>
                  <Image
                    source={{ uri: Globais.urladmin + item.foto }}
                    style={{ width: "100%", height: 60 }}
                  />
                </View>
                <View
                  style={{ margin: 2, flex: 0.8, justifyContent: "center" }}
                >
                  <Text style={styles.titulo}>{item.titulo}</Text>
                  <Text style={{ color: "#F38DAD" }}>
                    {" "}
                    {item.datanoticiaFormatada}
                  </Text>
                </View>
              </View>
              <Card.Divider />
            </TouchableOpacity>
          ))
        )}
      </Card>

      <Card>
        <Tab
          value={index}
          onChange={(e) => setIndex(e)}
          indicatorStyle={{
            backgroundColor: "white",
            height: 3,
          }}
          variant="primary"
        >
          <Tab.Item
            title="Informes"
            titleStyle={{ fontSize: 12 }}
            icon={{
              name: "newspaper-outline",
              type: "ionicon",
              color: "white",
            }}
          />
          <Tab.Item
            title="Você Sabia?"
            titleStyle={{ fontSize: 12 }}
            icon={{ name: "help-outline", type: "ionicon", color: "white" }}
          />
          <Tab.Item
            title="Quem Somos"
            titleStyle={{ fontSize: 12 }}
            icon={{
              name: "person-circle-outline",
              type: "ionicon",
              color: "white",
            }}
          />
        </Tab>

        <TabView value={index} onChange={setIndex} animationType="spring">
          <TabView.Item style={{ width: "85%" }}>
            <PageBlocoInformes navigation={navigation} />
          </TabView.Item>
          <TabView.Item style={{ backgroundColor: "blue", width: "85%" }}>
            <Text h1>Favorite</Text>
          </TabView.Item>
          <TabView.Item style={{ width: "85%" }}>
            <PageBlocoquemsomos navigation={navigation} />
          </TabView.Item>
        </TabView>
      </Card>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: StatusBar.currentHeight,
  },
  scrollView: {
    marginHorizontal: 0,
  },
  text: {
    fontSize: 35,
  },
  titulo: {
    color: "#43C1C4",
    fontSize: 16,
  },
  image: {
    width: "100%",
    height: 300,
    resizeMode: "contain",
  },
});
