import * as React from "react";
import { Text, TouchableOpacity, View } from "react-native";
import { NavigationContainer } from "@react-navigation/native";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { createDrawerNavigator } from "@react-navigation/drawer";

import Icone from "./Configs/Icone";
// (...)
const Tab = createBottomTabNavigator();
const Drawer = createDrawerNavigator();
function MenuDrawer(props) {
  return (
    <View>
      <Text>MENU NOVO</Text>
    </View>
  );
}

function DrawerNavigation() {
  return (
    <Drawer.Navigator
      initialRouteName="Home"
      drawerContent={(props) => MenuDrawer(props)}
    >
      <Drawer.Screen
        name="Home"
        component={TabNavigation}
        options={() => ({
          headerShown: false,
        })}
      />
      <Drawer.Screen name="Notifications" component={PageCurriculo} />
    </Drawer.Navigator>
  );
}

function MenuHeader({ navigation, titulo }) {
  return (
    <View
      style={{
        flexDirection: "row",
        height: 50,
        backgroundColor: "#574c91",
      }}
    >
      <View
        style={{
          flex: 0.8,
          alignContent: "center",
          justifyContent: "center",
          marginLeft: 5,
        }}
      >
        <TouchableOpacity onPress={() => navigation.openDrawer()}>
          <Icone color="#f28f66" estilo={{ FontSize: 12 }} name="bars" />
        </TouchableOpacity>
      </View>
      <View
        style={{
          flex: 1.5,
          alignContent: "flex-start",
          justifyContent: "center",
        }}
      >
        <Text style={{ color: "#f28f66", fontSize: 20 }}>{titulo}</Text>
      </View>
      <View
        style={{
          flex: 0.2,
          alignContent: "flex-end",
          justifyContent: "center",
        }}
      >
        <TouchableOpacity
          onPress={() => {
            navigation.navigate("TabVagas");
          }}
        >
          <Icone color="#f28f66" estilo={{ FontSize: 12 }} name="bell" />
        </TouchableOpacity>
      </View>
    </View>
  );
}

function PageHome({ navigation }) {
  return (
    <View>
      <MenuHeader navigation={navigation} titulo="Bem vindo a CentralRH" />
      <Text>Home</Text>
    </View>
  );
}
function PageVagas({ navigation }) {
  return (
    <View>
      <MenuHeader navigation={navigation} titulo="Confira nossas vagas" />
      <Text>Vagas</Text>
    </View>
  );
}
function PageCurriculo({ navigation }) {
  return (
    <View>
      <MenuHeader navigation={navigation} titulo="Baixe seu Curriculo" />
      <Text>Curriculo</Text>
    </View>
  );
}
function PageQuemsomos({ navigation }) {
  return (
    <View>
      <MenuHeader navigation={navigation} titulo="Quem somos nós" />
      <Text>Sobre nós</Text>
    </View>
  );
}
function TabNavigation() {
  return (
    <Tab.Navigator
      screenOptions={({ route }) => ({
        tabBarIcon: ({ focused, color, size }) => {
          let icone;

          if (route.name === "TabHome") {
            icone = focused ? (
              <Icone estilo={{}} color={"#f28f66"} name="home" />
            ) : (
              <Icone estilo={{}} color={"#574c91"} name="home" />
            );
          } else if (route.name === "TabVagas") {
            icone = focused ? (
              <Icone estilo={{}} color={"#f28f66"} name="search" />
            ) : (
              <Icone estilo={{}} color={"#574c91"} name="search" />
            );
          } else if (route.name === "TabCurriculo") {
            icone = focused ? (
              <Icone estilo={{}} color={"#f28f66"} name="address-card" />
            ) : (
              <Icone estilo={{}} color={"#574c91"} name="address-card" />
            );
          } else if (route.name === "TabQuemsomos") {
            icone = focused ? (
              <Icone estilo={{}} color={"#f28f66"} name="address-book" />
            ) : (
              <Icone estilo={{}} color={"#574c91"} name="address-book" />
            );
          }

          // You can return any component that you like here!
          return icone;
        },
        tabBarActiveTintColor: "tomato",
        tabBarInactiveTintColor: "gray",
      })}
    >
      <Tab.Screen
        name="TabHome"
        component={PageHome}
        options={() => ({ title: "Home", headerShown: false })}
      />
      <Tab.Screen
        name="TabVagas"
        component={PageVagas}
        options={() => ({ title: "Vagas", headerShown: false })}
      />
      <Tab.Screen
        name="TabCurriculo"
        component={PageCurriculo}
        options={() => ({ title: "Currículo", headerShown: false })}
      />
      <Tab.Screen
        name="TabQuemsomos"
        component={PageQuemsomos}
        options={() => ({ title: "Sobre Nós", headerShown: false })}
      />
    </Tab.Navigator>
  );
}
export default function App() {
  return (
    <NavigationContainer>
      <DrawerNavigation />
    </NavigationContainer>
  );
}
