import React, { useState } from "react";
import { StyleSheet, StatusBar, View } from "react-native";
import { SafeAreaProvider } from "react-native-safe-area-context";
import { ThemeProvider } from "react-native-elements";
import useCachedResources from "./hooks/useCachedResources";
import Musica from "./Pagesexternas/Musica";
import Globais from "./Configs/Globais";
import CalendarioNovo from "./Pages/CalendarioNovo";
import Inicio from "./Inicio";
const theme = {
  colors: {
    primary: "#5BA3D9",
    sedondary: "#43C1C4",
  },
};
export default function App() {
  const isLoadingComplete = useCachedResources();
  const [carregando, setCarregando] = useState(true);

  Globais.fontPorks();

  async function carregarmusica() {
    var url = Globais.url + "musica";
    fetch(url)
      .then((resp) => resp.json())
      .then((json) => {
        const result = json.data.data;
        var musicas = [];
        for (let i = 0; i < result.length; i++) {
          musicas.push({
            url_musica: Globais.urladminarquivos + result[i].arquivo,
            nome: result[i].nome,
          });
        }
        Globais.urlmusica = musicas[0].url_musica;
        Globais.playlist = musicas;
        console.log("playlist ok");
        setCarregando(false);
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {
        console.log("finaliza o carregamento");
        setCarregando(false);
      });
  }
  React.useEffect(() => {
    carregarmusica();
  }, []);

  if (carregando) {
    return null;
  } else {
    return (
      <ThemeProvider theme={theme}>
        <SafeAreaProvider style={{ backgroundColor: "#5BA3D9" }}>
          <View style={{ flex: 1, marginTop: 25 }}>
            <Inicio />
          </View>
        </SafeAreaProvider>
      </ThemeProvider>
    );
  }
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    backgroundColor: "#ECF0F1",
  },
  buttonsContainer: {
    padding: 10,
  },
  textStyle: {
    textAlign: "center",
    marginBottom: 8,
  },
});
