import React, { useEffect, useState } from "react";
import Icon from "react-native-vector-icons/FontAwesome";
import { Input, Card, Button } from "react-native-elements";
import { MaskedTextInput } from "react-native-mask-text";
import {
  View,
  Text,
  TextInput,
  StyleSheet,
  Image,
  TouchableOpacity,
} from "react-native";
import Globais from "../Configs/Globais";
import { color } from "react-native-reanimated";

export default ({ navigation, titulo }) => {
  useEffect(() => {
    console.log("AQUI CARREGA");
    carregarimagens();
  }, []);
  const [nome, setNome] = useState("");
  const [email, setEmail] = useState("maria@maria.com.brr");
  const [fone, setFone] = useState("");
  const [dados, setDados] = useState([]);
  const [temerro, settemErro] = useState(false);
  const [mensagem, setMensagem] = useState("");

  var dadosFormdata = new FormData();
  dadosFormdata.append("nome", email);
  dadosFormdata.append("email", email);
  dadosFormdata.append("fone", fone);
  dadosFormdata.append("mensagem", mensagem);
  console.log(dadosFormdata);
  var requestPost = {
    method: "POST",
    body: dadosFormdata,
  };

  function carregarimagens() {
    var url = Globais.url + "galeria";

    fetch(url)
      .then((resp) => resp.json())
      .then((json) => {
        const resultado = json.data.data;
        setDados(resultado);
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {
        //alert("fim");
      });
  }

  return (
    <View
      style={{
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <Card containerStyle={{ width: "90%", borderRadius: 10 }}>
        <Card.Title style={{ color: "#43C1C4" }}>Galeria</Card.Title>
        <Card.Divider />
        Galeria
      </Card>
    </View>
  );
};

const estilos = StyleSheet.create({
  image: {
    width: 300,
    height: 300,
    resizeMode: "contain",
  },
});
const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    backgroundColor: "#ecf0f1",
    padding: 8,
  },
  input: {
    height: 40,
    margin: 12,
    color: "#fff",
    backgroundColor: "#F38DAD",
    borderWidth: 1,
    borderColor: "#F38DAD",
  },
});
