import React, { useEffect, useState } from "react";
import Icon from "react-native-vector-icons/FontAwesome";
import { Input, Card, Button } from "react-native-elements";
import { View, Text, StyleSheet, Image } from "react-native";
import Globais from "../Configs/Globais";

export default ({ navigation, titulo }) => {
  const [email, setemail] = useState("");
  const [senha, setsenha] = useState("");
  const [dados, setDados] = useState([]);
  const [temerro, settemErro] = useState(false);
  const [mensagem, setmensagem] = useState("");
  var requestPost = {
    method: "POST",

    body: JSON.stringify({
      email: email,
      senha: senha,
    }),
  };

  function logincandidato() {
    fetch(Globais.url + "candidato/login", requestPost)
      .then((resp) => resp.json())
      .then((json) => {
        console.log(json.login);
        let dadosretorno = json.login;
        console.log(dadosretorno.message);
        if (dadosretorno.message === "error") {
          settemErro(true);
          setmensagem(dadosretorno.info);
        } else {
          Globais.nomeusuario = dadosretorno.nome;
          Globais.emailusuario = dadosretorno.email;
          Globais.logado = true;
          Globais.token = dadosretorno.token;
          Globais.fotousuario = dadosretorno.foto;
          localStorage.setItem("nomeusuario", dadosretorno.nome);
          localStorage.setItem("emailusuario", dadosretorno.email);
          localStorage.setItem("fotousuario", dadosretorno.foto);
          localStorage.setItem("logado", "true");
          localStorage.setItem("token", dadosretorno.token);

          setDados(json.login);
          navigation.navigate("TabHome");
        }
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {
        //alert("fim");
      });
  }

  return (
    <View>
      <Image
        style={estilos.image}
        source={require("../assets/images/logo.png")}
      />

      <Card>
        <Card.Title>
          {Globais.nomeusuario} {email}
        </Card.Title>
        <Card.Divider />
        <Input
          onChangeText={(texto) => {
            setemail(texto);
          }}
          value={email}
          placeholder="Email"
          leftIcon={{ type: "font-awesome", name: "user" }}
        />

        <Input
          secureTextEntry={true}
          placeholder="Senha"
          value={senha}
          onChangeText={(texto) => {
            setsenha(texto);
          }}
          leftIcon={<Icon name="lock" size={24} color="black" />}
        />
        <Button
          onPress={() => logincandidato()}
          icon={<Icon name="arrow-right" size={15} color="white" />}
          title="Entrar"
        />
        {temerro ? (
          <View
            style={{
              width: "100%",
              height: 15,
              alignContent: "center",
              alignItems: "center",
            }}
          >
            <Text
              style={{
                fontSize: 20,
                color: "red",
              }}
            >
              {mensagem}
            </Text>
          </View>
        ) : (
          <View></View>
        )}
      </Card>
    </View>
  );
};

const estilos = StyleSheet.create({
  image: {
    width: 250,
    height: 250,
  },
});
