import React, { useEffect, useState } from "react";
import Icon from "react-native-vector-icons/FontAwesome";
import { Input, Card, Button } from "react-native-elements";
import { MaskedTextInput } from "react-native-mask-text";

import {
  View,
  Text,
  ActivityIndicator,
  StyleSheet,
  Image,
  FlatList,
  TouchableOpacity,
} from "react-native";
import Globais from "../Configs/Globais";
import { color } from "react-native-reanimated";
const Pegaimagem = (props) => {
  return <h1>{props.item}</h1>;
};
export default ({ navigation, titulo }) => {
  useEffect(() => {
    carregarimagens();
  }, []);

  const [dados, setDados] = useState([]);
  const [carregando, setCarregando] = useState(false);

  function carregarimagens() {
    var url = Globais.url + "galeria";
    setCarregando(true);

    fetch(url)
      .then((resp) => resp.json())
      .then((json) => {
        const resultado = json.data.data;
        setDados(resultado);
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {
        setCarregando(false);
      });
  }

  return (
    <View
      style={{
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <Card containerStyle={{ width: "90%", borderRadius: 10 }}>
        <Card.Title style={{ color: "#43C1C4" }}>Galeria</Card.Title>
        <Card.Divider />
        <View style={{ width: "100%", flexDirection: "row" }}>
          {carregando ? (
            <ActivityIndicator size="large" />
          ) : (
            <FlatList
              data={dados}
              keyExtractor={(item) => item.id_galeria}
              renderItem={({ item }) => (
                <View style={{ width: "50%", height: "50%" }}>
                  <Image
                    source={{ uri: Globais.urladmin + item.foto }}
                    style={{ width: 200, height: 200 }}
                  />
                </View>
              )}
            />
          )}
        </View>
      </Card>
    </View>
  );
};
