import React, { useState } from "react";
import { View, Text } from "react-native";
import { Calendar } from "react-native-big-calendar";
import { Picker } from "@react-native-picker/picker";
import "../assets/locale/pt-br";

const events = [
  {
    title: "Encontro ok",
    id_noticia: 25,
    start: new Date("2022-01-21 00:00:00"),
    end: new Date("2022-01-21 00:00:00"),
  },
  {
    title: "OUTRO AQUI AGORA QUERO VER",
    id_noticia: 25,
    start: new Date("2022-01-07"),
    end: new Date("2022-01-07"),
  },
];
export default function CalendarioNovo() {
  const [estacao, setestacao] = useState("month");

  return (
    <View>
      <Picker
        selectedValue={estacao}
        onValueChange={(itemValue, itemIndex) => setestacao(itemValue)}
      >
        <Picker.Item label="Mensal" value="month" />
        <Picker.Item label="Semanal" value="week" />
        <Picker.Item label="Diário" value="day" />
        <Picker.Item label="3 Dias" value="3days" />
        <Picker.Item label="Cunstomizável" value="custom" />
      </Picker>
      <Calendar
        events={events}
        date={new Date(Date.now())}
        mode={estacao}
        height={600}
        activeDate={new Date(Date.now())}
        onPressCell={() => alert("ok")}
        onPressEvent={(data) => {
          alert(data.id_noticia);
        }}
        swipeEnabled={true}
        locale="pt-br"
        hideNowIndicator={true}
        showAllDayEventCell={true}
      />
    </View>
  );
}
