import React, { Component, useEffect, useState } from "react";
import { Alert, StyleSheet, Text, View, TouchableOpacity } from "react-native";
import Globais from "../Configs/Globais";
import { Agenda, LocaleConfig } from "react-native-calendars";

import { obterToken } from "../Configs/Globais";
LocaleConfig.locales.fr = {
  monthNames: [
    "Janeiro",
    "Fevereiro",
    "Março",
    "Abril",
    "Maio",
    "Junho",
    "Julho",
    "Agosto",
    "Setembro",
    "Outubro",
    "Novembro",
    "Dezembro",
  ],
  monthNamesShort: [
    "Jan.",
    "Fev.",
    "Mar",
    "Abr",
    "Mai",
    "Jun",
    "Jul.",
    "Ago",
    "Set.",
    "Out.",
    "Nov.",
    "Dez.",
  ],
  dayNames: [
    "Domingo",
    "Segunda",
    "Terça",
    "Quarta",
    "Quinta",
    "Sexta",
    "Sábado",
  ],
  dayNamesShort: ["Dom.", "Seg.", "Ter.", "Qua.", "Qui.", "Sex.", "Sáb."],
};

LocaleConfig.defaultLocale = "fr";
export default class AgendaScreen extends Component {
  state = {
    items: "",
  };

  componentDidMount() {
    console.log(Globais.token);
    this.carregarAgenda(Globais.token);
  }

  render() {
    // this.loadItems(this.state.token);

    //this.carregarAgenda(this.state.token);

    return (
      <Agenda
        testID={"agenda"}
        items={this.state.items}
        loadItemsForMonth={this.loadItems.bind(this)}
        selected={new Date()}
        renderItem={this.renderItem.bind(this)}
        renderEmptyDate={this.renderEmptyDate.bind(this)}
        rowHasChanged={this.rowHasChanged.bind(this)}
        showClosingKnob={true}
        // TESTES
        rowHasChanged={(row) => {
          console.log(row);
        }}
        renderEmptyDate={(row) => {
          console.log(row);
        }}
        onCalendarToggled={(calendarOpened) => {
          console.log(calendarOpened);
        }}
        onDayPress={(day) => {
          console.log(day);
        }}
        onDayChange={(day) => {
          console.log("day changed");
        }}
        theme={{
          calendarBackground: "#5BA3D9",
          agendaDayTextColor: "#F38DAD",
          agendaDayNumColor: "#43C1C4",
          agendaTodayColor: "#5BA3D9",
          agendaKnobColor: "white",
        }}
      />
    );
  }

  carregarAgenda(token) {
    var url = Globais.url + "v1/eventocalendario";
    fetch(url, {
      method: "get",
      headers: new Headers({
        Authorization: "Bearer " + token,
      }),
    })
      .then((resp) => resp.json())
      .then((json) => {
        this.setState({
          items: json,
        });
      })
      .catch((e) => {
        return null;
      })
      .finally(() => {
        return null;
      });
  }
  loadItems() {
    this.setState({
      items: this.state.items,
    });
  }

  renderItem(item) {
    return (
      <TouchableOpacity
        testID={"item"}
        style={[styles.item, { height: item.height }]}
        onPress={() => {
          console.log(item.datanoticia);
          Alert.alert(item.datanoticia);
        }}
      >
        <Text>{item.titulo}</Text>
      </TouchableOpacity>
    );
  }

  renderEmptyDate() {
    return (
      <View style={styles.emptyDate}>
        <Text>This is empty date!</Text>
      </View>
    );
  }

  rowHasChanged(r1, r2) {
    return r1.name !== r2.name;
  }

  timeToString(time) {
    const date = new Date(time);
    return date.toISOString().split("T")[0];
  }
}

const styles = StyleSheet.create({
  item: {
    backgroundColor: "white",
    flex: 1,
    borderRadius: 5,
    padding: 10,
    marginRight: 10,
    marginTop: 17,
  },
  emptyDate: {
    height: 15,
    flex: 1,
    paddingTop: 30,
  },
});
