import React, { useEffect, useState } from "react";
import Icon from "react-native-vector-icons/FontAwesome";
import { Input, Card, Button } from "react-native-elements";
import { MaskedTextInput } from "react-native-mask-text";

import {
  View,
  Text,
  ActivityIndicator,
  StyleSheet,
  Image,
  FlatList,
  TouchableOpacity,
} from "react-native";
import Globais from "../Configs/Globais";

export default ({ navigation, titulo }) => {
  useEffect(() => {
    carregarimagens();
  }, []);

  const [dados, setDados] = useState([]);
  const [carregando, setCarregando] = useState(false);

  function carregarimagens() {
    var url = Globais.url + "galeria";
    setCarregando(true);

    fetch(url)
      .then((resp) => resp.json())
      .then((json) => {
        const resultado = json.data.data;
        setDados(resultado);
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {
        setCarregando(false);
      });
  }

  return (
    <View
      style={{
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <Card containerStyle={{ width: "90%", borderRadius: 10 }}>
        <Card.Title style={{ color: "#43C1C4" }}>Galeria</Card.Title>
        <Card.Divider />

        <View style={{ width: "100%", flexDirection: "row" }}>
          {carregando ? (
            <ActivityIndicator size="large" />
          ) : (
            <FlatList
              data={dados}
              numColumns={3}
              keyExtractor={(item) => item.id_galeria.toString()}
              renderItem={({ item, index }) => (
                <TouchableOpacity
                  onPress={() => {
                    alert(item.foto);
                  }}
                >
                  <View style={{ margin: 2 }}>
                    <Image
                      source={{ uri: Globais.urladmin + item.foto }}
                      style={{ width: 100, height: 100 }}
                    />
                  </View>
                </TouchableOpacity>
              )}
            />
          )}
        </View>
      </Card>
    </View>
  );
};

const styles = StyleSheet.create({
  centeredView: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    marginTop: 22,
  },
  modalView: {
    margin: 20,
    backgroundColor: "white",
    borderRadius: 20,
    padding: 35,
    alignItems: "center",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5,
  },
  button: {
    borderRadius: 20,
    padding: 10,
    elevation: 2,
  },
  buttonOpen: {
    backgroundColor: "#F194FF",
  },
  buttonClose: {
    backgroundColor: "#2196F3",
  },
  textStyle: {
    color: "white",
    fontWeight: "bold",
    textAlign: "center",
  },
  modalText: {
    marginBottom: 15,
    textAlign: "center",
  },
});
