import React, { useEffect, useState } from "react";
import { Input, Card, Button } from "react-native-elements";
import {
  View,
  Modal,
  StatusBar,
  StyleSheet,
  ActivityIndicator,
  Image,
  Text,
  SafeAreaView,
  ScrollView,
  Pressable,
  Alert,
  FlatList,
  TouchableOpacity,
} from "react-native";
import MenuHeader from "../layouts/MenuHeader";
import Icone from "../Configs/Icone";
import { obterToken } from "../Configs/Globais";
import Globais from "../Configs/Globais";

export default ({ navigation, titulo }) => {
  const [dados, setDados] = useState("");
  const [dadoslogin, setDadoslogin] = useState("");
  const [dadosagenda, setDadosAgenda] = useState("");
  const [carregando, setCarregando] = useState(false);
  async function carregaragenda(token, id_turma) {
    var url = Globais.url + "v1/agenda/" + id_turma;
    setCarregando(true);

    await fetch(url, {
      method: "get",
      headers: new Headers({
        Authorization: "Bearer " + token,
      }),
    })
      .then((resp) => resp.json())
      .then((json) => {
        const resultado = json.data.data;
        setDadosAgenda(resultado);
        console.log(resultado);
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {
        setCarregando(false);
      });
  }

  async function carregarimagens(token) {
    var url = Globais.url + "v1/cardapio";
    setCarregando(true);
    //console.log(token);
    await fetch(url, {
      method: "get",
      headers: new Headers({
        Authorization: "Bearer " + token,
      }),
    })
      .then((resp) => resp.json())
      .then((json) => {
        const resultado = json.data.data;
        setDados(resultado);
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {
        setCarregando(false);
      });
  }

  useEffect(() => {
    obterToken("dadoslogin").then((resposta) => {
      carregarimagens(resposta.access_token);
      carregaragenda(resposta.access_token, resposta.id_turma);
    });
  }, []);

  return (
    <View>
      <MenuHeader navigation={navigation} titulo="Nosso Cantinho" />

      <ScrollView style={styles.scrollView}>
        <View
          style={{
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <Card containerStyle={{ width: "90%", borderRadius: 10 }}>
            <Card.Title style={{ color: "#43C1C4" }}>
              Cardapio da semana{" "}
            </Card.Title>
            <Card.Divider />

            {carregando ? (
              <ActivityIndicator size="large" />
            ) : (
              <FlatList
                data={dados}
                numColumns={1}
                keyExtractor={(item) => item.id_cardapio.toString()}
                renderItem={({ item, index }) => (
                  <TouchableOpacity
                    onPress={() => {
                      navigation.navigate("TabCardapio", {
                        id_cardapio: item.id_cardapio,
                      });
                    }}
                  >
                    <View style={{ width: "100%", flexDirection: "row" }}>
                      <View style={{ margin: 2, flex: 1 }}>
                        <Image
                          source={{ uri: Globais.urladmin + item.foto }}
                          style={{ width: "100%", height: 100 }}
                        />
                      </View>
                    </View>
                    <Card.Divider />
                  </TouchableOpacity>
                )}
              />
            )}

            <Card.Title style={{ color: "#43C1C4" }}>Agenda </Card.Title>
            <Card.Divider />
            {carregando ? (
              <ActivityIndicator size="large" />
            ) : (
              <FlatList
                data={dadosagenda}
                numColumns={1}
                keyExtractor={(item) => item.id_agenda.toString()}
                renderItem={({ item, index }) => (
                  <TouchableOpacity
                    onPress={() => {
                      navigation.navigate("PageVerAgenda", {
                        informacoes: item,
                      });
                    }}
                  >
                    <View style={{ width: "100%", flexDirection: "row" }}>
                      <View style={{ margin: 2, flex: 0.2 }}>
                        <Image
                          source={{ uri: Globais.urladmin + item.foto }}
                          style={{
                            width: "100%",
                            height: 100,
                            resizeMode: "contain",
                          }}
                        />
                      </View>
                      <View style={{ margin: 2, flex: 0.8 }}>
                        <Text style={styles.titulo}>{item.materia}</Text>
                        <Text style={styles.titulo}>
                          {item.dataagendaFormatada}
                        </Text>
                        {item.arquivo == "sem_arquivo.png" ? (
                          <View>
                            <Text></Text>
                          </View>
                        ) : (
                          <TouchableOpacity
                            onPress={() => {
                              Download(Globais.urladmin + item.arquivo);
                            }}
                          >
                            <Icone estilo={{}} color={"#5BA3D9"} name="file" />
                            <Text style={{ color: "#F38DAD" }}>Download</Text>
                          </TouchableOpacity>
                        )}
                      </View>
                    </View>
                    <Card.Divider />
                  </TouchableOpacity>
                )}
              />
            )}
          </Card>
        </View>
      </ScrollView>
    </View>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: StatusBar.currentHeight,
  },
  scrollView: {
    marginHorizontal: 0,
  },
  text: {
    fontSize: 42,
  },
  titulo: {
    color: "#43C1C4",
    fontSize: 20,
  },
});
