import AsyncStorage from "@react-native-async-storage/async-storage";
import * as WebBrowser from "expo-web-browser";
import { Dimensions } from "react-native";
import { useFonts } from "expo-font";
import { Audio } from "expo-av";
class Globais {
  // static url = "https://localhost/nossocantinho/adminapi/public/api/";
  /*static urladmin =
    "https://localhost/nossocantinho/admin/App/Lib/arquivos/fotos/";*/
  static url = "https://nossocantinho.swsagenciadigital.com.br/adminapi/api/";
  static urladmin =
    "https://nossocantinho.swsagenciadigital.com.br/admin/App/Lib/arquivos/fotos/";
  static urladminarquivos =
    "https://nossocantinho.swsagenciadigital.com.br/admin/App/Lib/arquivos/";
  static logado = false;
  static nomeusuario = "anonimo";
  static som = new Audio.Sound();
  static somStatus = null;
  static playSound = async (url) => {
    this.som.unloadAsync();
    this.urlmusica = url;
    this.somStatus = await this.som.loadAsync(
      { uri: url },
      { shouldPlay: true }
    );
    console.log(this.somStatus.isLoaded);
    if (this.somStatus.isLoaded) {
      return await this.som.playAsync();
    } else {
      return null;
    }
  };

  static stopSound = async (url) => {
    //console.log(this.som);
    for (let i = 0; i < Globais.playlist.length; i++) {
      Globais.playlist[i].current = 0;
    }
    return await this.som.stopAsync();
  };
  static urlmusica = "";
  static emailusuario = "anonimo@aninimo.com.br";
  static codigousuario = "0";
  static turma = "Turma não definida";
  static fotousuario =
    "data:image/png;base64,";
  static token = null;
  static phoneNumerWhatsapp = "557999067362";
  static Armazenar = (chave, valor) => {
    AsyncStorage.setItem(chave, valor);
  };
  static percentualaltura = (percentual) => {
    const dimensions = Dimensions.get("window");
    var altura = dimensions.height;
    altura = (altura * percentual) / 100;
    return altura;
  };
  static playlist = [];

  static fontPorks = () => {
    const [loaded] = useFonts({
      Porkys: require("../assets/fonts/Porkys.ttf"),
    });
  };

  static percentuallargura = (percentual) => {
    const dimensions = Dimensions.get("window");
    var largura = dimensions.width;
    largura = (largura * percentual) / 100;
    return largura;
  };
  static carregadados = async (token) => {
    var url = Globais.url + "v1/cardapio";

    // console.log(token);
    await fetch(url, {
      method: "get",
      headers: new Headers({
        Authorization: "Bearer " + token,
      }),
    })
      .then((resp) => resp.json())
      .then((json) => {
        const resultado = json.data.data;
        setDados(resultado);
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {});
  };
  static BuscarJson = async (chave) => {
    var jsonValue = await AsyncStorage.getItem(chave);

    if (jsonValue != null) {
      jsonValue = JSON.parse(jsonValue);
      // console.log(jsonValue.access_token);
      return jsonValue;
    } else {
      return null;
    }
  };
  static Download = async (url) => {
    await WebBrowser.openBrowserAsync(url);
  };
  static ArmazenarJson = async (chave, value) => {
    try {
      const jsonValue = JSON.stringify(value);
      await AsyncStorage.setItem(chave, jsonValue);
    } catch (e) {
      // saving error
    }
  };
  static limparvariaveis() {
    this.logado = false;
    this.nomeusuario = "anonimo";
    this.emailusuario = "";
    this.fotousuario =
      "data:image/png;base64,";
    this.token = null;
    this.ArmazenarJson("dadoslogin", null);
  }
}

export default Globais;
export const obterToken = async (chave) => {
  var jsonValue = await AsyncStorage.getItem(chave);

  if (jsonValue != null) {
    jsonValue = JSON.parse(jsonValue);

    return jsonValue;
  } else {
    return null;
  }
};
