import React, { useEffect, useState } from "react";
import Icon from "react-native-vector-icons/FontAwesome";
import { Input, Card, Button } from "react-native-elements";
import { View, Text } from "react-native";
import Globais from "../Configs/Globais";

export default ({ navigation, titulo }) => {
  const [email, setemail] = useState("");
  const [senha, setsenha] = useState("");
  const [dados, setDados] = useState([]);
  var requestPost = {
    method: "POST",

    body: JSON.stringify({
      email: email,
      senha: senha,
    }),
  };

  function logincandidato() {
    fetch(Globais.url + "candidato/login", requestPost)
      .then((resp) => resp.json())
      .then((json) => {
        console.log(json.login);
        let dadosretorno = json.login;
        console.log(dadosretorno.message);
        if (dadosretorno.message === "error") {
          alert("senha errada ne");
        } else {
          Globais.nomeusuario = dadosretorno.nome;
          Globais.emailusuario = dadosretorno.email;
          Globais.logado = true;
          Globais.token = dadosretorno.token;
          Globais.fotousuario = dadosretorno.foto;
          localStorage.setItem("nomeusuario", dadosretorno.nome);
          localStorage.setItem("emailusuario", dadosretorno.email);
          localStorage.setItem("fotousuario", dadosretorno.foto);
          localStorage.setItem("logado", "true");
          localStorage.setItem("token", dadosretorno.token);

          setDados(json.login);
          navigation.navigate("TabHome");
        }
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {
        alert("fim");
      });
  }

  return (
    <View>
      <Card>
        <Card.Title>
          {Globais.nomeusuario} {email}
        </Card.Title>
        <Card.Divider />
        <Input
          onChangeText={(texto) => {
            setemail(texto);
          }}
          value={email}
          placeholder="Email"
          leftIcon={{ type: "font-awesome", name: "user" }}
        />

        <Input
          secureTextEntry={true}
          placeholder="Senha"
          value={senha}
          onChangeText={(texto) => {
            setsenha(texto);
          }}
          leftIcon={<Icon name="lock" size={24} color="black" />}
        />
        <Button
          onPress={() => logincandidato()}
          icon={<Icon name="arrow-right" size={15} color="white" />}
          title="Entrar"
        />

        <Text style={{ width: "100%", fontSize: 15, color: "red" }}>
          Mensagem
        </Text>
      </Card>
    </View>
  );
};
