import React, { useEffect, useState } from "react";
import Icon from "react-native-vector-icons/FontAwesome";
import { Input, Card, Button } from "react-native-elements";
import { View, Text } from "react-native";
import Globais from "../Configs/Globais";

export default ({ navigation, titulo }) => {
  var nome = Globais.url;
  Globais.nomeusuario = "joao";
  Globais.limparvariaveis();

  const [email, setemail] = useState("");
  const [senha, setsenha] = useState("");
  const [dados, setDados] = useState([]);
  var requestPost = {
    method: "POST",

    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      email: email,
      senha: senha,
    }),
  };

  function logincandidato() {
    fetch(Globais.url + "candidato/login", requestPost)
      .then((resp) => resp.json())
      .then((json) => {
        console.log(json.login);
        let dadosretorno = json.login;
        Globais.nomeusuario = dadosretorno.nome;
        Globais.emailusuario = dadosretorno.email;
        Globais.token = dadosretorno.token;
        setDados(json.login);
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {
        alert("fim");
      });
  }

  return (
    <View>
      <Card>
        <Card.Title>
          {Globais.nomeusuario} {email}
        </Card.Title>
        <Card.Divider />
        <Input
          onChangeText={(texto) => {
            setemail(texto);
          }}
          value={email}
          placeholder="Email"
          leftIcon={{ type: "font-awesome", name: "user" }}
        />

        <Input
          secureTextEntry={true}
          placeholder="Senha"
          value={senha}
          onChangeText={(texto) => {
            setsenha(texto);
          }}
          leftIcon={<Icon name="lock" size={24} color="black" />}
        />
        <Button
          onPress={() => logincandidato()}
          icon={<Icon name="arrow-right" size={15} color="white" />}
          title="Entrar"
        />
      </Card>
    </View>
  );
};
