import React, { useState } from "react";
import { View, Text, TouchableOpacity } from "react-native";
import { Avatar, Badge, Icon, withBadge } from "react-native-elements";
import Globais from "../Configs/Globais";
import AsyncStorage from "@react-native-async-storage/async-storage";
export default ({ navigation }) => {
  function limpaacesso() {
    Globais.limparvariaveis();
    navigation.navigate("PageInicioApp");
  }
  return (
    <View style={{ flexDirection: "column" }}>
      <View
        style={{
          width: "100%",
          alignContent: "center",
          alignItems: "center",
        }}
      >
        <Avatar
          size="xlarge"
          title="LW"
          source={{
            uri: Globais.urladmin + Globais.fotousuario,
          }}
          onPress={() => console.log("asdf!")}
          activeOpacity={0.7}
        />
        <Text>{Globais.nomeusuario}</Text>
        <Text>{Globais.turma}</Text>
        <Text>{Globais.emailusuario}</Text>
        <Text>
          <TouchableOpacity
            onPress={() => navigation.navigate("PageAlterarsenha")}
          >
            Alterar Senha
          </TouchableOpacity>
        </Text>
        {Globais.logado ? (
          <TouchableOpacity onPress={() => limpaacesso()}>
            <Text>Sair</Text>
          </TouchableOpacity>
        ) : (
          <TouchableOpacity
            onPress={() => navigation.navigate("PageInicioApp")}
          >
            <Text>Login</Text>
          </TouchableOpacity>
        )}
      </View>
    </View>
  );
};
