import React, { useState } from "react";
import { View, Text, Picker } from "react-native";
import { Calendar } from "react-native-big-calendar";
var data = new Date(Date.now());

import "../assets/locale/pt-br";
console.log(data);
var data2 = new Date(2022, 0, 6, 0, 0);

const events = [
  {
    title: "Encontro ok",
    id_noticia: 25,
    start: new Date("2022-01-06"),
    end: new Date("2022-01-07"),
  },
  {
    title: "OUTRO AQUI AGORA QUERO VER",
    id_noticia: 25,
    start: new Date("2022-01-06"),
    end: new Date("2022-01-06"),
  },
  {
    title: "novo encontro",
    id_noticia: 20,
    start: new Date("2022-01-07"),
    end: new Date("2022-01-08"),
  },
  {
    title: "Coffee break",
    id_noticia: 30,
    start: new Date("2022-01-08"),
    end: new Date("2022-01-09"),
  },
];
export default function CalendarioNovo() {
  const [estacao, setestacao] = useState("month");
  return (
    <View>
      <Text>show calend√°rio</Text>
      <Calendar
        events={events}
        date={new Date(Date.now())}
        mode={estacao}
        height={600}
        activeDate={new Date("2022-01-06")}
        onPressCell={() => alert("ok")}
        onPressEvent={(data) => {
          alert(data.id_noticia);
        }}
        swipeEnabled={true}
        locale="pt-br"
        hideNowIndicator={true}
        showAllDayEventCell={true}
      />
    </View>
  );
}
