import React, { Component, useEffect, useState } from "react";
import { Alert, StyleSheet, Text, View, TouchableOpacity } from "react-native";
import Globais from "../Configs/Globais";
import { Agenda, LocaleConfig } from "react-native-calendars";

import { obterToken } from "../Configs/Globais";
LocaleConfig.locales.fr = {
  monthNames: [
    "Janeiro",
    "Fevereiro",
    "Março",
    "Abril",
    "Maio",
    "Junho",
    "Julho",
    "Agosto",
    "Setembro",
    "Outubro",
    "Novembro",
    "Dezembro",
  ],
  monthNamesShort: [
    "Jan.",
    "Fev.",
    "Mar",
    "Abr",
    "Mai",
    "Jun",
    "Jul.",
    "Ago",
    "Set.",
    "Out.",
    "Nov.",
    "Dez.",
  ],
  dayNames: [
    "Domingo",
    "Segunda",
    "Terça",
    "Quarta",
    "Quinta",
    "Sexta",
    "Sábado",
  ],
  dayNamesShort: ["Dom.", "Seg.", "Ter.", "Qua.", "Qui.", "Sex.", "Sáb."],
};
async function carregaragenda(token) {
  var url = Globais.url + "v1/eventocalendario";

  console.log(url);
  await fetch(url, {
    method: "get",
    headers: new Headers({
      Authorization: "Bearer " + token,
    }),
  })
    .then((resp) => resp.json())
    .then((json) => {
      const resultado = json;
      console.log(resultado);

      return resultado;
    })
    .catch((e) => {
      return null;
    })
    .finally(() => {
      return null;
    });
}
LocaleConfig.defaultLocale = "fr";
export default class AgendaScreen extends Component {
  state = {
    items: "",
    token: "",
  };

  componentDidMount() {
    obterToken("dadoslogin").then((resposta) => {
      this.setState({
        token: resposta.access_token,
      });
    });
    carregaragenda(resposta.access_token);
  }

  render() {
    return (
      <Agenda
        testID={"agenda"}
        items={this.state.items}
        loadItemsForMonth={this.loadItems.bind(this)}
        selected={"2021-12-23"}
        renderItem={this.renderItem.bind(this)}
        renderEmptyDate={this.renderEmptyDate.bind(this)}
        rowHasChanged={this.rowHasChanged.bind(this)}
        showClosingKnob={true}
        // TESTES
        rowHasChanged={(row) => {
          console.log(row);
        }}
        renderEmptyDate={(row) => {
          console.log(row);
        }}
        onCalendarToggled={(calendarOpened) => {
          console.log(calendarOpened);
        }}
        onDayPress={(day) => {
          console.log(day);
        }}
        onDayChange={(day) => {
          console.log("day changed");
        }}
        theme={{
          calendarBackground: "#5BA3D9",
          agendaDayTextColor: "#F38DAD",
          agendaDayNumColor: "#43C1C4",
          agendaTodayColor: "#5BA3D9",
          agendaKnobColor: "white",
        }}
      />
    );
  }

  loadItems() {
    this.setState({
      items: {
        "2021-12-28": [
          {
            id_noticia: 2,
            titulo:
              "Pílula do câncer: STF declara inconstitucional lei que autorizava medicação",
            texto:
              "O Supremo Tribunal Federal (STF) declarou, por maioria, inconstitucional a Lei que autorizava o uso da chamada “pílula do câncer”. O julgamento se deu por meio de sessão virtual, encerrada na noite da última segunda-feira (23/10)\r\n                    \r\n                ",
            foto: "Marco-Aurelio-1-1.jpg-2021-12-12-12-01.jpg",
          },
        ],
        "2021-12-30": [
          {
            id_noticia: 3,
            titulo: "​É tempo de alegria",
            texto: "asdfadfadf",
            foto: "postagem_11_horas.jpg-2021-12-18-13-30.jpg",
          },
        ],
        "2021-12-31": [
          {
            id_noticia: 4,
            titulo: 'Alunos do Ensino Médio participam de um "Papo Sério"',
            texto: "asdfasdfadf",
            foto: "postagem_sabado.jpg-2021-12-18-13-58.jpg",
          },
        ],
      },
    });
  }

  renderItem(item) {
    return (
      <TouchableOpacity
        testID={"item"}
        style={[styles.item, { height: item.height }]}
        onPress={() => {
          console.log(item.datanoticia);
          Alert.alert(item.datanoticia);
        }}
      >
        <Text>{item.titulo}</Text>
      </TouchableOpacity>
    );
  }

  renderEmptyDate() {
    return (
      <View style={styles.emptyDate}>
        <Text>This is empty date!</Text>
      </View>
    );
  }

  rowHasChanged(r1, r2) {
    return r1.name !== r2.name;
  }

  timeToString(time) {
    const date = new Date(time);
    return date.toISOString().split("T")[0];
  }
}

const styles = StyleSheet.create({
  item: {
    backgroundColor: "white",
    flex: 1,
    borderRadius: 5,
    padding: 10,
    marginRight: 10,
    marginTop: 17,
  },
  emptyDate: {
    height: 15,
    flex: 1,
    paddingTop: 30,
  },
});
