import React, { useEffect, useState } from "react";
import {
  ScrollView,
  View,
  Image,
  FlatList,
  Text,
  StatusBar,
  StyleSheet,
  SafeAreaView,
  ActivityIndicator,
  Touchable,
} from "react-native";
import { TouchableOpacity } from "react-native-gesture-handler";

import { Card } from "react-native-elements";
import Banner from "./Banner";
import Globais from "../Configs/Globais";
export default ({ navigation }) => {
  useEffect(() => {
    carregarimagens();
  }, []);

  const [dados, setDados] = React.useState([]);
  const [carregando, setCarregando] = React.useState(false);

  function carregarimagens() {
    var url = Globais.url + "informes";
    setCarregando(true);

    fetch(url)
      .then((resp) => resp.json())
      .then((json) => {
        const resultado = json.data.data;
        setDados(resultado);
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {
        setCarregando(false);
      });
  }
  return (
    <ScrollView>
      <Card>
        <Card.Image
          style={styles.image}
          source={require("../assets/images/logo.png")}
        />
        <Banner />
      </Card>
      <Card>
        <Card.Title style={{ color: "#43C1C4" }}>Últimas Notícias</Card.Title>
        <Card.Divider />
        <SafeAreaView style={{ flex: 1 }}>
          {carregando ? (
            <ActivityIndicator size="large" />
          ) : (
            <FlatList
              data={dados}
              numColumns={1}
              ListHeaderComponent={
                <View>
                  <Text>Header</Text>
                </View>
              }
              ListFooterComponent={
                <View>
                  <Text>Footer</Text>
                </View>
              }
              keyExtractor={(item) => item.id_noticia.toString()}
              renderItem={({ item, index }) => (
                <TouchableOpacity
                  onPress={() => {
                    navigation.navigate("PageVerinformeexterno", {
                      informacoes: item,
                    });
                  }}
                >
                  <View style={{ width: "100%", flexDirection: "row" }}>
                    <View style={{ margin: 2, flex: 0.2 }}>
                      <Image
                        source={{ uri: Globais.urladmin + item.foto }}
                        style={{ width: "100%", height: 80 }}
                      />
                    </View>
                    <View
                      style={{ margin: 2, flex: 0.8, justifyContent: "center" }}
                    >
                      <Text style={styles.titulo}>{item.titulo}</Text>
                    </View>
                  </View>
                  <Card.Divider />
                </TouchableOpacity>
              )}
            />
          )}
        </SafeAreaView>
      </Card>

      {dados.map((item, index) => {
        <Text>{item.titulo}</Text>;
      })}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: StatusBar.currentHeight,
  },
  scrollView: {
    marginHorizontal: 0,
  },
  text: {
    fontSize: 35,
  },
  titulo: {
    color: "#43C1C4",
    fontSize: 16,
  },
  image: {
    width: "100%",
    height: 300,
    resizeMode: "contain",
  },
});
