import React, { useEffect, useState } from "react";
import { Input, Card, Button } from "react-native-elements";
import {
  View,
  Modal,
  StatusBar,
  StyleSheet,
  ActivityIndicator,
  Image,
  Text,
  SafeAreaView,
  ScrollView,
  Pressable,
  Alert,
  FlatList,
  Dimensions,
  TouchableOpacity,
} from "react-native";
import MenuHeader from "../layouts/MenuHeader";
import WebView from "react-native-webview";
import { obterToken } from "../Configs/Globais";
import Globais from "../Configs/Globais";
import * as WebBrowser from "expo-web-browser";
import AsyncStorage from "@react-native-async-storage/async-storage";
export default ({ navigation, titulo }) => {
  const percentual = 20;
  const dimensions = Dimensions.get("window");
  var imageHeight = dimensions.height;
  imageHeight = imageHeight - (imageHeight * percentual) / 100;
  console.log(imageHeight);
  const imageWidth = dimensions.width;

  var tamanho = imageWidth - (imageWidth * percentual) / 100;
  const [dados, setDados] = useState([]);
  const [dadoslogin, setDadoslogin] = useState("");
  const [carregando, setCarregando] = useState(false);
  async function carregarimagens(token) {
    var url = Globais.url + "v1/cardapio";

    setCarregando(true);
    //console.log(token);
    await fetch(url, {
      method: "get",
      headers: new Headers({
        Authorization: "Bearer " + token,
      }),
    })
      .then((resp) => resp.json())
      .then((json) => {
        const resultado = json.data.data;
        setDados(resultado);
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {
        setCarregando(false);
      });
  }

  useEffect(() => {
    obterToken("dadoslogin").then((resposta) => {
      carregarimagens(resposta.access_token);
      setDadoslogin(resposta);
    });
  }, []);

  return (
    <View>
      <MenuHeader navigation={navigation} titulo="Confira nosso cardapio" />

      <ScrollView style={styles.scrollView}>
        <View
          style={{
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <Card containerStyle={{ width: "90%", borderRadius: 10 }}>
            <Card.Title style={{ color: "#43C1C4" }}>
              Cardapio da semana{" "}
            </Card.Title>
            <Card.Divider />

            {carregando ? (
              <ActivityIndicator size="large" />
            ) : (
              dados.map((item, index) => (
                <TouchableOpacity
                  onPress={() => {
                    Download(Globais.urladmin + item.foto);
                  }}
                  key={item.id_cardapio}
                >
                  <View style={{ width: "100%", flexDirection: "row" }}>
                    <View style={{ margin: 2, flex: 1 }}>
                      <Image
                        source={{ uri: Globais.urladmin + item.foto }}
                        style={{
                          width: "100%",
                          height: imageHeight,
                          resizeMode: "stretch",
                        }}
                      />
                    </View>
                  </View>
                  <Card.Divider />
                </TouchableOpacity>
              ))
            )}
          </Card>
        </View>
      </ScrollView>
    </View>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: StatusBar.currentHeight,
  },
  scrollView: {
    marginHorizontal: 0,
  },
  text: {
    fontSize: 42,
  },
  titulo: {
    color: "#43C1C4",
    fontSize: 20,
  },
});
