import React from "react";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";

import Icone from "../Configs/Icone";

import PageInicio from "../Pagesexternas/PageInicio";
import PageGaleria from "../Pagesexternas/PageGaleria";
import PageAgendaDiaria from "../Pagesexternas/PageAgendaDiaria";
import PageFaleconosco from "../Pagesexternas/PageFaleconosco";
import PageInformes from "../Pagesexternas/PageInformes";
import PageLogin from "../Pages/PageLogin";

const Tab = createBottomTabNavigator();
export default () => {
  return (
    <Tab.Navigator
      screenOptions={({ route }) => ({
        tabBarIcon: ({ focused, color, size }) => {
          let icone;

          if (route.name === "TabHomeexterna") {
            icone = focused ? (
              <Icone estilo={{}} color={"red"} name="home" />
            ) : (
              <Icone estilo={{}} color={"#5BA3D9"} name="home" />
            );
          } else if (route.name === "TabAgendaDiaria") {
            icone = focused ? (
              <Icone estilo={{}} color={"#F38DAD"} name="calendar" />
            ) : (
              <Icone estilo={{}} color={"#5BA3D9"} name="calendar-pen" />
            );
          } else if (route.name === "TabInformes") {
            icone = focused ? (
              <Icone estilo={{}} color={"#F38DAD"} name="address-card" />
            ) : (
              <Icone estilo={{}} color={"#5BA3D9"} name="address-book" />
            );
          } else if (route.name === "TabFaleconosco") {
            icone = focused ? (
              <Icone estilo={{}} color={"#F38DAD"} name="envelope" />
            ) : (
              <Icone estilo={{}} color={"#5BA3D9"} name="envelope-square" />
            );
          } else if (route.name === "TabLogin") {
            icone = focused ? (
              <Icone estilo={{}} color={"#F38DAD"} name="unlock" />
            ) : (
              <Icone estilo={{}} color={"#5BA3D9"} name="lock" />
            );
          }
          // You can return any component that you like here!
          return icone;
        },
        tabBarActiveTintColor: "tomato",
        tabBarInactiveTintColor: "gray",
        tabBarItemStyle: {},
      })}
    >
      <Tab.Screen
        name="TabHomeexterna"
        component={PageInicio}
        options={() => ({ title: "Notícias", headerShown: false })}
      />
      <Tab.Screen
        name="TabGaleria"
        component={PageGaleria}
        options={() => ({ title: "Agenda Diária", headerShown: false })}
      />
      <Tab.Screen
        name="TabInformes"
        component={PageInformes}
        options={() => ({ title: "Galeria", headerShown: false })}
      />
      <Tab.Screen
        name="TabFaleconosco"
        component={PageFaleconosco}
        options={() => ({ title: "Boletim", headerShown: false })}
      />
      <Tab.Screen
        name="TabLogin"
        component={PageLogin}
        options={() => ({ title: "Fale conosco", headerShown: false })}
      />
    </Tab.Navigator>
  );
};
