import React, { useState, useEfecct } from "react";
import { View, Text } from "react-native";
import { Calendar } from "react-native-big-calendar";
import { Picker } from "@react-native-picker/picker";
import Globais from "../Configs/Globais";
import "../assets/locale/pt-br";

const events = [
  {
    id_noticia: "1",
    title: "Direito à saúde e a responsabilidade do Estado",
    foto: "noticia1.jpeg-2021-12-12-11-14.jpeg",
    datanoticia: "10/01/2022",
    start: new Date("2022-01-11 00:00:00"),
    end: new Date("2022-01-11 00:00:00"),
  },
  {
    id_noticia: "2",
    title:
      "Pílula do câncer: STF declara inconstitucional lei que autorizava medicação",
    foto: "Marco-Aurelio-1-1.jpg-2021-12-12-12-01.jpg",
    datanoticia: "19/01/2022",
    start: new Date("2022-01-20 00:00:00"),
    end: new Date("2022-01-20 00:00:00"),
  },
  {
    id_noticia: "3",
    title: "​É tempo de alegria",
    foto: "postagem_11_horas.jpg-2021-12-18-13-30.jpg",
    datanoticia: "20/01/2022",
    start: new Date("2022-01-21 00:00:00"),
    end: new Date("2022-01-21 00:00:00"),
  },
  {
    id_noticia: "4",
    title: "Alunos do Ensino Médio participam de um Papo Sério",
    foto: "postagem_sabado.jpg-2021-12-18-13-58.jpg",
    datanoticia: "20/01/2022",
    start: new Date("2022-01-21 00:00:00"),
    end: new Date("2022-01-21 00:00:00"),
  },
];
export default function CalendarioNovo() {
  const [estacao, setestacao] = useState("month");

  return (
    <View>
      <Picker
        selectedValue={estacao}
        onValueChange={(itemValue, itemIndex) => setestacao(itemValue)}
      >
        <Picker.Item label="Mensal" value="month" />
        <Picker.Item label="Semanal" value="week" />
        <Picker.Item label="Diário" value="day" />
        <Picker.Item label="3 Dias" value="3days" />
        <Picker.Item label="Cunstomizável" value="custom" />
      </Picker>
      <Calendar
        events={events}
        date={new Date(Date.now())}
        mode={estacao}
        height={600}
        activeDate={new Date(Date.now())}
        onPressCell={() => alert("ok")}
        onPressEvent={(data) => {
          alert(data.id_noticia);
        }}
        swipeEnabled={true}
        locale="pt-br"
        hideNowIndicator={true}
        showAllDayEventCell={true}
      />
    </View>
  );
}
