import React, { Component } from "react";
import { Alert, StyleSheet, Text, View, TouchableOpacity } from "react-native";

import { Agenda, LocaleConfig } from "react-native-calendars";
LocaleConfig.locales.fr = {
  monthNames: [
    "Janeiro",
    "Fevereiro",
    "Março",
    "Abril",
    "Maio",
    "Junho",
    "Julho",
    "Agosto",
    "Setembro",
    "Outubro",
    "Novembro",
    "Dezembro",
  ],
  monthNamesShort: [
    "Jan.",
    "Fev.",
    "Mar",
    "Abr",
    "Mai",
    "Jun",
    "Jul.",
    "Ago",
    "Set.",
    "Out.",
    "Nov.",
    "Dez.",
  ],
  dayNames: [
    "Domingo",
    "Segunda",
    "Terça",
    "Quarta",
    "Quinta",
    "Sexta",
    "Sábado",
  ],
  dayNamesShort: ["Dom.", "Seg.", "Ter.", "Qua.", "Qui.", "Sex.", "Sáb."],
};

LocaleConfig.defaultLocale = "fr";
export default class AgendaScreen extends Component {
  state = {
    items: "",
  };

  render() {
    return (
      <Agenda
        testID={"agenda"}
        items={this.state.items}
        loadItemsForMonth={this.loadItems.bind(this)}
        selected={"2021-12-23"}
        renderItem={this.renderItem.bind(this)}
        renderEmptyDate={this.renderEmptyDate.bind(this)}
        rowHasChanged={this.rowHasChanged.bind(this)}
        showClosingKnob={true}
        theme={{ calendarBackground: "#5BA3D9", agendaKnobColor: "white" }}

        // TESTES
        // TESTES
      />
    );
  }

  loadItems(day) {
    this.setState({
      items: {
        "2021-12-23": [
          {
            name: "Chegando odia",
            start: "2021-12-23T13:45:00",
            end: "2021-12-23 19:45",
          },
          {
            name: "Whats up second Stuff",
            start: "2020-06-09T18:45:00",
            end: "2020-06-09 21:45",
          },
        ],

        "2021-12-25": [
          {
            name: "Natal em familia",
            start: "2021-12-25T13:45:00",
            end: "2021-12-25 19:45",
          },
          {
            name: "Whats up second Stuff",
            start: "2021-12-25T18:45:00",
            end: "2021-12-25 21:45",
          },
        ],
      },
    });
  }

  renderItem(item) {
    return (
      <TouchableOpacity
        testID={"item"}
        style={[styles.item, { height: item.height }]}
        onPress={() => Alert.alert(item.name)}
      >
        <Text>{item.name}</Text>
      </TouchableOpacity>
    );
  }

  renderEmptyDate() {
    return (
      <View style={styles.emptyDate}>
        <Text>This is empty date!</Text>
      </View>
    );
  }

  rowHasChanged(r1, r2) {
    return r1.name !== r2.name;
  }

  timeToString(time) {
    const date = new Date(time);
    return date.toISOString().split("T")[0];
  }
}

const styles = StyleSheet.create({
  item: {
    backgroundColor: "white",
    flex: 1,
    borderRadius: 5,
    padding: 10,
    marginRight: 10,
    marginTop: 17,
  },
  emptyDate: {
    height: 15,
    flex: 1,
    paddingTop: 30,
  },
});
