import React, { useEffect, useState } from "react";
import Icon from "react-native-vector-icons/FontAwesome";
import { Input, Card, Button } from "react-native-elements";
import { MaskedTextInput } from "react-native-mask-text";
import WebView from "react-native-webview";
import {
  View,
  Modal,
  StatusBar,
  StyleSheet,
  ActivityIndicator,
  Image,
  Text,
  useWindowDimensions,
  SafeAreaView,
  ScrollView,
  Pressable,
  Alert,
  FlatList,
  TouchableOpacity,
} from "react-native";
import Globais from "../Configs/Globais";
import Banner from "./Banner";

export default ({ route, navigation, titulo }) => {
  useEffect(() => {
    carregarmateria();
  }, []);
  const { width } = useWindowDimensions();
  const [modalVisible, setModalVisible] = useState(false);
  const [dados, setDados] = useState([]);
  const [carregando, setCarregando] = useState(false);
  console.log(route.params);
  const id_noticia = route.params.id_noticia;

  function carregarmateria() {
    var url = Globais.url + "informe/" + id_noticia;
    setCarregando(true);

    fetch(url)
      .then((resp) => resp.json())
      .then((json) => {
        const resultado = json.data.data;
        console.log(resultado);
        setDados(resultado);
        // console.log(resultado);
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {
        setCarregando(false);
      });
  }

  return (
    <ScrollView style={styles.scrollView}>
      <View
        style={{
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        {carregando ? (
          <ActivityIndicator size="large" animating={true} />
        ) : (
          <Card containerStyle={{ width: "90%", borderRadius: 10 }}>
            <Card.Title style={{ color: "#43C1C4" }}>
              <TouchableOpacity
                onPress={() => navigation.navigate("PageInicioApp")}
              >
                <Icon
                  name="arrow-left"
                  style={{ color: "#F38DAD", fontSize: 22 }}
                />
                <View>
                  <Text> {dados.titulo}</Text>
                </View>
              </TouchableOpacity>
            </Card.Title>
            <Card.Divider />
            <Card.Image source={{ uri: Globais.urladmin + dados.foto }} />
            <Card.Divider />
            <View>
              <WebView
                source={{ html: dados.texto }}
                style={{ marginTop: 20 }}
                containerStyle={{
                  width: "100%",
                  height: 500,
                  backgroundColor: "white",
                  flex: 1,
                }}
              />
            </View>
          </Card>
        )}
      </View>
    </ScrollView>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: StatusBar.currentHeight,
  },
  scrollView: {
    marginHorizontal: 0,
  },
  text: {
    fontSize: 42,
  },
  titulo: {
    color: "#43C1C4",
    fontSize: 20,
  },
  a: {
    fontWeight: "300",
    color: "#FF3366", // make links coloured pink
  },
});

const tagsStyles = {
  body: {
    whiteSpace: "normal",
    color: "#43C1C4",
  },
  a: {
    color: "green",
  },
};
