import React, { useEffect, useState } from "react";
import { View, Text } from "react-native";
import MenuHeader from "../layouts/MenuHeader";
import Banner from "../layouts/Banner";
import Globais from "../Configs/Globais";
import AsyncStorage from "@react-native-async-storage/async-storage";
export default ({ navigation, titulo }) => {
  const [dados, setDados] = useState("");
  const BuscarJson = async (chave) => {
    var jsonValue = await AsyncStorage.getItem(chave);

    if (jsonValue != null) {
      jsonValue = JSON.parse(jsonValue);
      setDados(jsonValue);
      return jsonValue;
    } else {
      return null;
    }
  };
  useEffect(() => {
    BuscarJson("dadoslogin");
  }, []);
  return (
    <View>
      <MenuHeader navigation={navigation} titulo="PÃ¡gina inicial" />

      <ScrollView style={styles.scrollView}>
        <View
          style={{
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <Card containerStyle={{ width: "90%", borderRadius: 10 }}>
            <Card.Title style={{ color: "#43C1C4" }}>Informes</Card.Title>
            <Card.Divider />

            {carregando ? (
              <ActivityIndicator size="large" />
            ) : (
              <FlatList
                data={dados}
                numColumns={1}
                keyExtractor={(item) => item.id_noticia.toString()}
                renderItem={({ item, index }) => (
                  <TouchableOpacity
                    onPress={() => {
                      navigation.navigate("PageVerinformeexterno", {
                        id_noticia: item.id_noticia,
                      });
                    }}
                  >
                    <View style={{ width: "100%", flexDirection: "row" }}>
                      <View style={{ margin: 2, flex: 0.3 }}>
                        <Image
                          source={{ uri: Globais.urladmin + item.foto }}
                          style={{ width: "100%", height: 100 }}
                        />
                      </View>
                      <View style={{ margin: 2, flex: 0.7 }}>
                        <Text style={styles.titulo}>{item.titulo}</Text>
                      </View>
                    </View>
                    <Card.Divider />
                  </TouchableOpacity>
                )}
              />
            )}
          </Card>
        </View>
      </ScrollView>
    </View>
  );
};
