import React, { useState } from "react";
import { View, Text, TouchableOpacity } from "react-native";
import { Avatar, Badge, Icon, withBadge } from "react-native-elements";
import Globais from "../Configs/Globais";
export default ({ navigation }) => {
  //const [email, setemail] = useState("");
  //const [senha, setsenha] = useState("");
  //const [dados, setDados] = useState([]);
  const foto = localStorage.getItem("fotousuario")?.toString();

  return (
    <View style={{ flexDirection: "column" }}>
      <View
        style={{ width: "100%", alignContent: "center", alignItems: "center" }}
      >
        <Avatar
          size="xlarge"
          title="LW"
          source={{
            uri: foto,
          }}
          onPress={() => console.log("asdf!")}
          activeOpacity={0.7}
        />
        <Text>{Globais.nomeusuario}</Text>
        <Text>{Globais.emailusuario}</Text>
        {Globais.logado}:(
        <TouchableOpacity onPress={() => navigation.navigate("PageLogin")}>
          <Text>Login</Text>
        </TouchableOpacity>
        ):(
        <TouchableOpacity onPress={() => navigation.navigate("PageLogin")}>
          <Text>Login</Text>
        </TouchableOpacity>
        )
      </View>
    </View>
  );
};
