import React, { useEffect, useState } from "react";
import Icon from "react-native-vector-icons/FontAwesome";
import { Input, Card, Button } from "react-native-elements";
import { MaskedTextInput } from "react-native-mask-text";

import {
  View,
  Modal,
  StatusBar,
  StyleSheet,
  ActivityIndicator,
  Image,
  Text,
  SafeAreaView,
  ScrollView,
  Pressable,
  Alert,
  FlatList,
  TouchableOpacity,
} from "react-native";
import Globais from "../Configs/Globais";
import Banner from "./Banner";

export default ({ route, navigation, titulo }) => {
  useEffect(() => {
    carregarimagens();
  }, []);

  const [modalVisible, setModalVisible] = useState(false);
  const [dados, setDados] = useState([]);
  const [carregando, setCarregando] = useState(false);
  console.log(route.params);
  const id_noticia = route.params.id_noticia;
  function carregarimagens() {
    var url = Globais.url + "informes";
    setCarregando(true);

    fetch(url)
      .then((resp) => resp.json())
      .then((json) => {
        const resultado = json.data.data;
        setDados(resultado);
        // console.log(resultado);
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {
        setCarregando(false);
      });
  }

  return (
    <ScrollView style={styles.scrollView}>
      <View
        style={{
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <Card containerStyle={{ width: "90%", borderRadius: 10 }}>
          <Card.Title style={{ color: "#43C1C4" }}>Informes</Card.Title>
          <Card.Divider />

          {carregando ? (
            <ActivityIndicator size="large" />
          ) : (
            <FlatList
              data={dados}
              numColumns={1}
              keyExtractor={(item) => item.id_noticia.toString()}
              renderItem={({ item, index }) => (
                <TouchableOpacity
                  onPress={() => {
                    navigation.navigate("PageVerinformeexterno", {
                      id_noticia: item.id_noticia,
                    });
                  }}
                >
                  <View style={{ width: "100%", flexDirection: "row" }}>
                    <View style={{ margin: 2, flex: 0.3 }}>
                      <Image
                        source={{ uri: Globais.urladmin + item.foto }}
                        style={{ width: "100%", height: 100 }}
                      />
                    </View>
                    <View style={{ margin: 2, flex: 0.7 }}>
                      <Text style={styles.titulo}>{item.titulo}</Text>
                    </View>
                  </View>
                  <Card.Divider />
                </TouchableOpacity>
              )}
            />
          )}
        </Card>
      </View>
    </ScrollView>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: StatusBar.currentHeight,
  },
  scrollView: {
    marginHorizontal: 0,
  },
  text: {
    fontSize: 42,
  },
  titulo: {
    color: "#43C1C4",
    fontSize: 20,
  },
});
