import React, { useEffect, useState } from "react";
import Icon from "react-native-vector-icons/FontAwesome";
import { Input, Card, Button } from "react-native-elements";
import { MaskedTextInput } from "react-native-mask-text";

import {
  View,
  Modal,
  StatusBar,
  StyleSheet,
  ActivityIndicator,
  Image,
  Text,
  SafeAreaView,
  ScrollView,
  Pressable,
  Alert,
  FlatList,
  TouchableOpacity,
} from "react-native";
import Globais from "../Configs/Globais";
import Banner from "./Banner";

export default ({ navigation, titulo }) => {
  useEffect(() => {
    carregarimagens();
  }, []);
  const [modalVisible, setModalVisible] = useState(false);
  const [dados, setDados] = useState([]);
  const [carregando, setCarregando] = useState(false);

  function carregarimagens() {
    var url = Globais.url + "informes";
    setCarregando(true);

    fetch(url)
      .then((resp) => resp.json())
      .then((json) => {
        const resultado = json.data.data;
        setDados(resultado);
        // console.log(resultado);
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {
        setCarregando(false);
      });
  }

  return (
    <ScrollView style={styles.scrollView}>
      <View
        style={{
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <Card containerStyle={{ width: "95%", borderRadius: 10 }}>
          <Card.Title style={{ color: "#43C1C4" }}>Informes</Card.Title>
          <Card.Divider />

          {carregando ? (
            <ActivityIndicator size="large" />
          ) : (
            dados.map((item, index) => (
              <TouchableOpacity
                onPress={() => {
                  navigation.navigate("PageVerinformeexterno", {
                    informacoes: item,
                  });
                }}
                key={item.id_noticia}
              >
                <View style={{ width: "100%", flexDirection: "row" }}>
                  <View style={{ flex: 0.2 }}>
                    <Image
                      source={{ uri: Globais.urladmin + item.foto }}
                      style={{
                        flex: 1,
                        width: "100%",
                        height: "100%",
                        aspectRatio: 1,
                        resizeMode: "contain",
                      }}
                    />
                  </View>
                  <View
                    style={{
                      margin: 8,
                      flex: 0.8,
                      justifyContent: "center",
                      alignItems: "left",
                    }}
                  >
                    <Text style={styles.titulo}>{item.titulo}</Text>
                  </View>
                </View>
                <Card.Divider />
              </TouchableOpacity>
            ))
          )}
        </Card>
      </View>
    </ScrollView>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: StatusBar.currentHeight,
  },
  scrollView: {
    marginHorizontal: 0,
  },

  titulo: {
    color: "#43C1C4",
    fontSize: 13,
  },
});
