import React, { useEffect, useState } from "react";
import { Input, Card, Button } from "react-native-elements";
import {
  View,
  Modal,
  StatusBar,
  StyleSheet,
  ActivityIndicator,
  Image,
  Text,
  SafeAreaView,
  ScrollView,
  Pressable,
  Alert,
  FlatList,
  Dimensions,
  TouchableOpacity,
} from "react-native";
import MenuHeader from "../layouts/MenuHeader";

import { obterToken } from "../Configs/Globais";
import Globais from "../Configs/Globais";
import AsyncStorage from "@react-native-async-storage/async-storage";
export default ({ navigation, titulo }) => {
  const dimensions = Dimensions.get("window");
  const imageHeight = Math.round((dimensions.width * 9) / 16);
  console.log(imageHeight);
  const imageWidth = dimensions.width;
  const percentual = 10;
  var tamanho = imageWidth - (imageWidth * percentual) / 100;
  const [dados, setDados] = useState("");
  const [dadoslogin, setDadoslogin] = useState("");
  const [carregando, setCarregando] = useState(false);
  async function carregarimagens(token) {
    var url = Globais.url + "v1/cardapio";
    setCarregando(true);
    //console.log(token);
    await fetch(url, {
      method: "get",
      headers: new Headers({
        Authorization: "Bearer " + token,
      }),
    })
      .then((resp) => resp.json())
      .then((json) => {
        const resultado = json.data.data;
        setDados(resultado);
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {
        setCarregando(false);
      });
  }

  useEffect(() => {
    obterToken("dadoslogin").then((resposta) => {
      carregarimagens(resposta.access_token);
      setDadoslogin(resposta);
    });
  }, []);

  return (
    <View>
      <MenuHeader navigation={navigation} titulo="Confira nosso cardapio" />

      <ScrollView style={styles.scrollView}>
        <View
          style={{
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <Card containerStyle={{ width: "90%", borderRadius: 10 }}>
            <Card.Title style={{ color: "#43C1C4" }}>
              Cardapio da semana{" "}
            </Card.Title>
            <Card.Divider />

            {carregando ? (
              <ActivityIndicator size="large" />
            ) : (
              <FlatList
                data={dados}
                numColumns={1}
                keyExtractor={(item) => item.id_cardapio.toString()}
                renderItem={({ item, index }) => (
                  <TouchableOpacity
                    onPress={() => {
                      navigation.navigate("PageVerinformeexterno", {
                        id_cardapio: item.id_cardapio,
                      });
                    }}
                  >
                    <View style={{ width: "100%", flexDirection: "row" }}>
                      <View style={{ margin: 2, flex: 1 }}>
                        <Image
                          source={{ uri: Globais.urladmin + item.foto }}
                          style={{
                            width: "100%",
                            height: imageHeight,
                            resizeMode: "contain",
                          }}
                        />
                      </View>
                    </View>
                    <Card.Divider />
                  </TouchableOpacity>
                )}
              />
            )}
          </Card>
        </View>
      </ScrollView>
    </View>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: StatusBar.currentHeight,
  },
  scrollView: {
    marginHorizontal: 0,
  },
  text: {
    fontSize: 42,
  },
  titulo: {
    color: "#43C1C4",
    fontSize: 20,
  },
});
