import React, { useEffect, useState } from "react";
import { ScrollView, View, StatusBar, StyleSheet } from "react-native";

import Icon from "react-native-vector-icons/Ionicons";
import { TouchableOpacity } from "react-native-gesture-handler";

import { Card, Tab, TabView, FAB } from "react-native-elements";

import BannerPrincipal from "./BannerPrincipal";
import Globais from "../Configs/Globais";
import PageBlocoquemsomos from "./PageBlocoquemsomos";
import PageBlocoInformes from "./PageBlocoInformes";
import PageBlocovcsabia from "./PageBlocovcsabia";
import { Audio } from "expo-av";
export default ({ navigation }) => {
  const [index, setIndex] = React.useState(0);
  const [som, setSom] = React.useState(new Audio.Sound());
  const [somStatus, setSomStatus] = React.useState(null);
  const [urlmusica, setUrlMusica] = React.useState("");
  const [carregando, setCarregando] = React.useState(false);
  const [ligado, setLigado] = React.useState(true);
  useEffect(() => {
    carregarmusica();
  }, []);
  function ligadesliga() {
    console.log(som);
    console.log(somStatus);
    let status;
    if (somStatus.isLoaded && ligado) {
      status = som.pauseAsync();
    }
    if (somStatus.isLoaded && !ligado) {
      status = som.playAsync();
    }
    setSomStatus(status);
    setLigado(!ligado);
  }
  async function playinicial(url) {
    console.log(url);
    console.log(som);
    const status = await som.loadAsync({ uri: url }, { shouldPlay: true });
    setSomStatus(status);

    if (!status.isPlaying) {
      return await som.playAsync();
    }
  }
  function carregarmusica() {
    var url = Globais.url + "musica";
    setCarregando(true);

    fetch(url)
      .then((resp) => resp.json())
      .then((json) => {
        const resultado = Globais.urladminarquivos + json.data.data[0].arquivo;
        // console.log(resultado);
        setUrlMusica(resultado);
        playinicial(resultado);
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {
        setCarregando(false);
      });
  }
  return (
    <ScrollView>
      <Card
        containerStyle={{
          borderRadius: 10,
        }}
      >
        <Card.Image
          style={styles.image}
          source={require("../assets/images/logo.png")}
        />
      </Card>
      <View
        style={{
          flex: 1,
          alignSelf: "flex-end",
          marginTop: 10,
          marginRight: 20,

          position: "absolute",
        }}
      >
        <TouchableOpacity onPress={() => ligadesliga()}>
          {ligado ? (
            <Icon
              name="musical-notes"
              style={{ fontSize: 40, color: "#F38DAD" }}
            />
          ) : (
            <Icon
              name="musical-notes-outline"
              style={{ fontSize: 40, color: "#F38DAD" }}
            />
          )}
        </TouchableOpacity>
      </View>
      <BannerPrincipal />

      <Card>
        <Tab
          value={index}
          onChange={(e) => setIndex(e)}
          indicatorStyle={{
            backgroundColor: "white",
            height: 3,
          }}
          variant="primary"
        >
          <Tab.Item
            title="Informes"
            titleStyle={{ fontSize: 12 }}
            icon={{
              name: "newspaper-outline",
              type: "ionicon",
              color: "white",
            }}
          />
          <Tab.Item
            title="VocÃª Sabia?"
            titleStyle={{ fontSize: 12 }}
            icon={{ name: "help-outline", type: "ionicon", color: "white" }}
          />
          <Tab.Item
            title="Quem Somos"
            titleStyle={{ fontSize: 12 }}
            icon={{
              name: "person-circle-outline",
              type: "ionicon",
              color: "white",
            }}
          />
        </Tab>

        <TabView value={index} onChange={setIndex} animationType="spring">
          <TabView.Item style={{ width: "85%" }}>
            <PageBlocoInformes navigation={navigation} />
          </TabView.Item>
          <TabView.Item style={{ width: "85%" }}>
            <PageBlocovcsabia navigation={navigation} />
          </TabView.Item>
          <TabView.Item style={{ width: "85%" }}>
            <PageBlocoquemsomos navigation={navigation} />
          </TabView.Item>
        </TabView>
      </Card>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: StatusBar.currentHeight,
  },
  scrollView: {
    marginHorizontal: 0,
  },
  text: {
    fontSize: 35,
  },
  titulo: {
    color: "#43C1C4",
    fontSize: 16,
  },
  image: {
    width: "100%",
    height: 300,
    resizeMode: "contain",
  },
  actionButtonIcon: {
    fontSize: 20,
    height: 22,
    color: "white",
  },
});
