import React, { useEffect, useState } from "react";
import Icon from "react-native-vector-icons/FontAwesome";
import { Input, Card, Button } from "react-native-elements";

import {
  View,
  Modal,
  StatusBar,
  StyleSheet,
  ActivityIndicator,
  Image,
  Text,
  ScrollView,
  TouchableOpacity,
} from "react-native";
import Globais from "../Configs/Globais";
import Banner from "./Banner";

export default ({ navigation, titulo }) => {
  useEffect(() => {
    carregarimagens();
  }, []);
  const [modalVisible, setModalVisible] = useState(false);
  const [dados, setDados] = useState([]);
  const [carregando, setCarregando] = useState(false);

  function carregarimagens() {
    var url = Globais.url + "quemsomos";
    setCarregando(true);

    fetch(url)
      .then((resp) => resp.json())
      .then((json) => {
        const resultado = json.data.data;
        setDados(resultado);
        // console.log(resultado);
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {
        setCarregando(false);
      });
  }

  return (
    <ScrollView style={styles.scrollView}>
      <View
        style={{
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
      
          dados.map((item, index) => (
            <TouchableOpacity
              onPress={() => {
                navigation.navigate("PageVerinformeexterno", {
                  informacoes: item,
                });
              }}
              key={item.id_noticia}
            >
              <View style={{ width: "100%", flexDirection: "row" }}>
                <View style={{ flex: 0.2 }}>
                  <Image
                    source={{ uri: Globais.urladmin + item.foto }}
                    style={{
                      width: "100%",
                      height: Globais.percentualaltura(8),
                    }}
                  />
                </View>
                <View
                  style={{ margin: 2, flex: 0.8, justifyContent: "center" }}
                >
                  <Text style={styles.titulo}>{item.titulo}</Text>
                  <Text style={{ color: "#F38DAD" }}>
                    {item.datanoticiaFormatada}
                  </Text>
                </View>
              </View>
              <Card.Divider />
            </TouchableOpacity>
          ))
 
      </View>
    </ScrollView>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: StatusBar.currentHeight,
  },

  titulo: {
    color: "#43C1C4",
    fontSize: 13,
  },
});
