import React, { useState, useEffect } from "react";
import { View, Text } from "react-native";
import { Calendar } from "react-native-big-calendar";
import { Picker } from "@react-native-picker/picker";
import Globais from "../Configs/Globais";
import "../assets/locale/pt-br";

export default function CalendarioNovo({ navigation }) {
  function abreevento(data) {
    navigation.navigate("PageVerEvento", {
      informacoes: data,
    });
  }
  function carregarEventos(token) {
    var url = Globais.url + "v1/eventocalendario";

    fetch(url, {
      method: "get",
      headers: new Headers({
        Authorization: "Bearer " + token,
      }),
    })
      .then((resp) => resp.json())
      .then((json) => {
        var resposta = json;
        for (let i = 0; i < resposta.length; i++) {
          resposta[i].start = new Date(resposta[i].start);
          resposta[i].end = new Date(resposta[i].end);
        }
        setEventos(resposta);
      })
      .catch((e) => {
        console.log("ERRO" + e);
      })
      .finally(() => {
        console.log("FIM");
      });
  }
  const [estacao, setestacao] = useState("month");
  const [eventos, setEventos] = useState([
    {
      title: "novo evento",
      id_noticia: 3,
      start: new Date(Date.now()),
      end: new Date(Date.now()),
    },
  ]);

  useEffect(() => {
    carregarEventos(Globais.token);
  }, []);

  return (
    <View>
      <Picker
        selectedValue={estacao}
        onValueChange={(itemValue, itemIndex) => setestacao(itemValue)}
      >
        <Picker.Item label="Mensal" value="month" />
        <Picker.Item label="Semanal" value="week" />
        <Picker.Item label="Diário" value="day" />
        <Picker.Item label="3 Dias" value="3days" />
        <Picker.Item label="Cunstomizável" value="custom" />
      </Picker>
      <Calendar
        events={eventos}
        date={new Date(Date.now())}
        mode={estacao}
        height={600}
        activeDate={new Date(Date.now())}
        onPressEvent={(data) => {
          abreevento(data);
        }}
        swipeEnabled={true}
        locale="pt-br"
        hideNowIndicator={false}
        showAllDayEventCell={true}
      />
    </View>
  );
}
