import React, { useEffect, useState } from "react";
import Icon from "react-native-vector-icons/FontAwesome";
import { Input, Card, Button } from "react-native-elements";
import { MaskedTextInput } from "react-native-mask-text";
import WebView from "react-native-webview";
import Icone from "../Configs/Icone";

import RenderHtml from "react-native-render-html";
import {
  View,
  Modal,
  StatusBar,
  StyleSheet,
  ActivityIndicator,
  Image,
  Text,
  useWindowDimensions,
  SafeAreaView,
  ScrollView,
  Pressable,
  Alert,
  FlatList,
  TouchableOpacity,
} from "react-native";
import Globais from "../Configs/Globais";
import * as WebBrowser from "expo-web-browser";
export default ({ route, navigation, titulo }) => {
  async function Download(url) {
    await WebBrowser.openBrowserAsync(url);
  }
  useEffect(() => {
    setDados(informacoes);
  }, []);
  const { width } = useWindowDimensions();
  const [modalVisible, setModalVisible] = useState(false);
  const [dados, setDados] = useState([]);
  const [carregando, setCarregando] = useState(false);

  const informacoes = route.params.informacoes;

  return (
    <ScrollView style={styles.scrollView}>
      <View
        style={{
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        {carregando ? (
          <View
            style={{
              width: Globais.percentuallargura(85),
              height: Globais.percentualaltura(85),
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <ActivityIndicator size="large" animating={true} />
          </View>
        ) : (
          <Card containerStyle={{ width: "90%", borderRadius: 10 }}>
            <Card.Title style={{ color: "#43C1C4" }}>
              <TouchableOpacity
                onPress={() => navigation.navigate("TabEventos")}
              >
                <Icon
                  name="arrow-left"
                  style={{ color: "#F38DAD", fontSize: 22 }}
                />
                <View>
                  <Text> {dados.titulo}</Text>
                </View>
              </TouchableOpacity>
            </Card.Title>
            <Card.Divider />
            <Card.Image source={{ uri: Globais.urladmin + dados.foto }} />
            <Card.Divider />

            <RenderHtml
              contentWidth={width}
              source={{
                html:
                  dados.texto != ""
                    ? "<span>" + dados.texto + "</span>"
                    : "<b>semconteudo</b>",
              }}
            />
          </Card>
        )}
      </View>
    </ScrollView>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: StatusBar.currentHeight,
  },
  scrollView: {
    marginHorizontal: 0,
  },
  text: {
    fontSize: 42,
  },
  titulo: {
    color: "#43C1C4",
    fontSize: 20,
  },
  a: {
    fontWeight: "300",
    color: "#FF3366", // make links coloured pink
  },
});
