import AsyncStorage from "@react-native-async-storage/async-storage";
import * as WebBrowser from "expo-web-browser";
import { Dimensions } from "react-native";
class Globais {
  // static url = "https://localhost/nossocantinho/adminapi/public/api/";
  /*static urladmin =
    "https://localhost/nossocantinho/admin/App/Lib/arquivos/fotos/";*/
  static url = "https://nossocantinho.swsagenciadigital.com.br/adminapi/api/";
  static urladmin =
    "https://nossocantinho.swsagenciadigital.com.br/admin/App/Lib/arquivos/fotos/";
  static logado = false;
  static nomeusuario = "anonimo";
  static emailusuario = "anonimo@aninimo.com.br";
  static fotousuario =
    "data:image/png;base64,";
  static token = null;
  static Armazenar = (chave, valor) => {
    AsyncStorage.setItem(chave, valor);
  };
  static percentualaltura = (percentual) => {
    const dimensions = Dimensions.get("window");
    var altura = dimensions.height;
    altura = (altura * percentual) / 100;
    return altura;
  };

  static percentuallargura = (percentual) => {
    const dimensions = Dimensions.get("window");
    var largura = dimensions.width;
    largura = (largura * percentual) / 100;
    return largura;
  };
  static carregadados = async (token) => {
    var url = Globais.url + "v1/cardapio";

    console.log(token);
    await fetch(url, {
      method: "get",
      headers: new Headers({
        Authorization: "Bearer " + token,
      }),
    })
      .then((resp) => resp.json())
      .then((json) => {
        const resultado = json.data.data;
        setDados(resultado);
      })
      .catch((e) => {
        alert("erro" + e);
      })
      .finally(() => {});
  };
  static BuscarJson = async (chave) => {
    var jsonValue = await AsyncStorage.getItem(chave);

    if (jsonValue != null) {
      jsonValue = JSON.parse(jsonValue);
      // console.log(jsonValue.access_token);
      return jsonValue;
    } else {
      return null;
    }
  };
  static Download = async (url) => {
    await WebBrowser.openBrowserAsync(url);
  };
  static ArmazenarJson = async (chave, value) => {
    try {
      const jsonValue = JSON.stringify(value);
      await AsyncStorage.setItem(chave, jsonValue);
    } catch (e) {
      // saving error
    }
  };
  static limparvariaveis() {
    this.logado = false;
    this.nomeusuario = "anonimo";
    this.emailusuario = "";
    this.fotousuario =
      "data:image/png;base64,";
    this.token = null;
    this.ArmazenarJson("dadoslogin", null);
  }
}

export default Globais;
export const obterToken = async (chave) => {
  var jsonValue = await AsyncStorage.getItem(chave);

  if (jsonValue != null) {
    jsonValue = JSON.parse(jsonValue);

    return jsonValue;
  } else {
    return null;
  }
};
